import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewLevel: React.FC = () => {
    type ViewLevelParams = {
        id: string;
    };
    const [question, setQuestion] = useState([]);
    const [total_question, setTotalQuestion] = useState('');
    const [mark, setMark] = useState('');
    const [level_id, setLevel_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [duration, setDuration] = useState('');

    const history = useHistory();
    const { id } = useParams<ViewLevelParams>();

    useEffect(() => {

        fetch(`${baseUrl}${api.viewLevel}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setLevel_name(result.level_name);
                setMark(result.mark);
                setDuration(result.duration);
                setQuestion(result.question);
                setTotalQuestion(result.question.length);
                setLevel_id(result.id);

            })
            .catch(function (error) {
                console.log(error);
            })

    }, [])
    const start = () => {
        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(res => { history.push("/dashboard/view/question") })

            .catch(function (error) {
                console.log(error);
            })

    }

    return (
        <>
            <IonContent>

                <IonCard>
                    <IonCardHeader>
                        <IonButton fill="clear" onClick={() => history.goBack()}> <i className="bi bi-arrow-left"></i></IonButton>
                        <IonCardTitle>{level_name}</IonCardTitle>
                    </IonCardHeader>

                    <IonCardContent>
                        <IonItem>
                            Per Question Mark: {mark}
                        </IonItem>
                        <IonItem>
                            Total Time: {duration}
                        </IonItem>
                        <IonItem>
                            Total question: {total_question}
                        </IonItem>
                    </IonCardContent>

                    <div className='text-center mt-2 mb-3'>
                        <IonButton fill="outline" onClick={start}> <i className="bi bi-box-arrow-right"> start</i></IonButton>
                    </div>


                </IonCard>

            </IonContent>

        </>

    );
}
export default ViewLevel;
