import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
import { Typography } from '@mui/material';
type InstructorData = {
    id: string;
    name: string;
    email: string;

};


const Instructor: FC = () => {
    const [instructor, setInstructor] = useState([]);
    const [modalOpen, setModalOpen] = useState(false);
    const fetchInstructor = () => {
        fetch(`${baseUrl}${api.allInstructor}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setInstructor(resp.reverse())
            })
    }
    useEffect(() => {
        fetchInstructor();
    }, [])

    const columns = useMemo<MRT_ColumnDef<InstructorData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                enableColumnOrdering: false,
                size: 80,
            },

            {
                accessorKey: 'name',
                header: 'Instructor Name',

            },
            {
                accessorKey: 'email',
                header: 'Instructor Email',

            },

        ],
        [],
    );

    const deleteInstructor = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteInstructor}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        fetchInstructor();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Instructors</h1>
                <MaterialReactTable
                    columns={columns}
                    data={instructor}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton onClick={() => {
                                    setCourseData(row.original)
                                    setModalOpen(true);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteInstructor(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-Instructor">
                            <IconButton onClick={() => setModalOpen(true)}>
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />
                <CreateInstructor
                    open={modalOpen}
                    onClose={() => {
                        setModalOpen(false)

                    }}
                    fetchInstructor={() => fetchInstructor()}

                />
            </div>
        </IonContent>

    );

};

const CreateInstructor: FC<{
    onClose: () => void;
    fetchInstructor: () => void;
    open: boolean;

}> = (props) => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [error, setError] = useState("");
    const addInstructor = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'name': name,
                'email': email,

            }
            fetch(`${baseUrl}${api.addInstructor}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create instructor Successfully');
                        props.fetchInstructor();
                        props.onClose();
                        setError("");

                    }
                    else {
                        if (!name || !email) {
                            setError("Please fill all input Field");
                        }

                    }
                })

                .catch(error => console.log('error', error));

        }
    }


    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">Create Instructor</DialogTitle>
            <DialogContent>
                <form onSubmit={(e) => e.preventDefault()}>
                    <Stack
                        sx={{
                            width: '100%',
                            minWidth: { xs: '300px', sm: '360px', md: '400px' },
                            gap: '1.5rem',
                        }}
                    >

                        <TextField sx={{ mt: 3 }}
                            type="text"
                            label="Instructor Name"
                            variant="filled"
                            focused
                            onChange={(e: any) => setName(e.target.value)} />

                        <TextField sx={{ mt: 3 }}
                            type="email"
                            label="Instructor Email"
                            variant="filled"
                            focused
                            onChange={(e: any) => setEmail(e.target.value)} />

                    </Stack>
                </form>
            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={addInstructor}>
                    Save
                </Button>
            </DialogActions>
        </Dialog>
    );
}

export default Instructor;
