import {
    IonCard,
    IonCardContent,
    IonCardHeader,
    IonButton,
    IonToolbar,
    IonTitle,
    IonInput,
    IonItem,
    IonLabel,
    IonHeader
} from "@ionic/react";
import React, { Component } from "react";
import { Redirect } from 'react-router-dom';
import './Login.css';
import baseUrl, { api } from "../pages/Urls";

export default class Login extends Component<any, any>{
    constructor(props: any) {
        super(props);

        this.state = {
            email: '',
            password: '',
            token: '',
            redirectToReferrer: false,
            role: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.login = this.login.bind(this);
    }


    handleChange(e: React.ChangeEvent<any>) {
        this.setState({
            [e.target.name]: e.target.value,

        })
    }



    login(e: React.ChangeEvent<any>) {
        e.preventDefault();
        const { email, password, token, role } = this.state;
        const data = {
            email: email,
            password: password,
        };
        if (email && password) {

            fetch(`${baseUrl}${api.login}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',

                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        return response.json();
                    }
                    else {
                        throw Error([response.status, response.status].join(' '));
                    }
                })
                .then(result => {
                    if (result.token) {
                        //console.log(token);
                        localStorage.setItem('token', result.token);
                        this.setState({ redirectToReferrer: true });
                    }
                    if (result.role) {

                        localStorage.setItem('role', result.role);
                        this.setState({ redirectToReferrer: true });
                    }

                })
                .catch(error => console.log('error', error));
        }
        else {
            alert("Cannot be Empty")
        }
    }

    render() {
        if (localStorage.getItem('token')) {
            if (localStorage.getItem('role') == 'ar_staff2')
                return (<Redirect to={'/dashboard'} />) //admin
            return (<Redirect to={'/blog'} />)
        }

        return (
            <div className="contain">
                <IonCard>
                    <IonCardHeader>
                        <IonHeader>
                            <IonToolbar>
                                <IonTitle>Login</IonTitle>
                            </IonToolbar>
                        </IonHeader>
                    </IonCardHeader>

                    <IonCardContent>
                        <IonItem>
                            <IonLabel position="stacked">Email</IonLabel>
                            <IonInput type="text"
                                name="email"
                                value={this.state.email}
                                placeholder="enter email"
                                required
                                clearInput
                                onIonChange={(e: any) => this.handleChange(e)}
                            >
                            </IonInput>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Password</IonLabel>
                            <IonInput type="password"
                                name="password"
                                value={this.state.password}
                                placeholder="password"
                                required
                                clearInput
                                onIonChange={(e: any) => this.handleChange(e)}
                            >
                            </IonInput>
                        </IonItem>
                        <div style={{ marginTop: "3%" }}>
                            <IonButton color="primary" onClick={(e: any) => this.login(e)}>Login</IonButton>
                        </div>

                    </IonCardContent>
                </IonCard>
            </div>
        );
    }
}
