import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect } from 'react';
import MaterialReactTable, {
    MRT_ColumnDef,

} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Stack,
    Tooltip,
} from '@mui/material';
import { Add, Delete, Edit, Visibility } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Collapse } from 'react-bootstrap'
import AddLevel from './AddLevel';
type levelData = {
    id: string;
    level_name: string;


};


const TchrLevel: FC<{
    topic: any,
    level: any,
    course_name: string,
    fetchShowCourse: () => void
}> = (props) => {

    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const [modalOpen, setModalOpen] = useState(false);
    const [ltopic, setLTopic] = useState([]);
    const [level_name, setLevel_name] = useState('');
    const [id, setId] = useState('');
    const columns = useMemo<MRT_ColumnDef<levelData>[]>(
        () => [


            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },


        ],
        [],
    );
    const editLevel = (id: any) => {

        fetch(`${baseUrl}${api.showLevel}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setLevel_name(result.level.level_name);
                setId(result.level.id);
                setLTopic(

                    result.level.ltopic.map((lt: any) => (lt.topic_name))

                );
            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateLevel = () => {
        let data = {
            'ltopic': ltopic,
            'level_name': level_name,


        }
        fetch(`${baseUrl}${api.updateLevel}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    props.fetchShowCourse();
                    setCollapse(false);

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteLevel = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteLevel}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        props.fetchShowCourse();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }


    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>

            <AddLevel
                open={open}
                onClose={() => setOpen(false)}
                fetchShowCourse={() => props.fetchShowCourse()}
                topic={props.topic}
                course_name={props.course_name}

            />
            <Collapse in={collapse}>
                <div id="collapseID">
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Level</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Level Name</IonLabel>
                                    <IonInput type="text"
                                        name="level"
                                        value={level_name}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setLevel_name(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Topic</IonLabel>
                                    <IonSelect
                                        name="topic"
                                        value={ltopic}
                                        onIonChange={(e: any) => setLTopic(e.target.value)}

                                        placeholder="Select topic" multiple={true}>
                                        {props.topic.map((item: any, i: any) => (
                                            < IonSelectOption key={i.toString()}>{item.topic_name}</IonSelectOption>
                                        ))}
                                    </IonSelect>

                                </IonItem>


                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="danger" onClick={() => setCollapse(false)}>
                                        Cancel
                                    </IonButton>
                                    <IonButton color="primary" onClick={updateLevel}>
                                        Save
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </div>
            </Collapse>
            <h1 className='text-center mb-2'>List of Levels</h1>
            <MaterialReactTable
                columns={columns}
                data={props.level}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                enableRowNumbers
                enableColumnOrdering
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="Edit">
                            <IconButton onClick={() => {
                                editLevel(row.id)
                                setCollapse(!collapse);
                            }}>
                                <Edit />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="left" title="View" >
                            <IconButton onClick={() => {
                                editLevel(row.id)
                                setModalOpen(true);

                            }}>

                                <Visibility />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteLevel(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add-Course">
                        <IconButton aria-expanded={open} onClick={() => setOpen(!open)}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}
            />
            <ViewLevel
                open={modalOpen}
                onClose={() => {
                    setModalOpen(false)

                }}
                level_name={level_name}
                level_topic={ltopic}
            />
        </div>


    );

};

const ViewLevel: FC<{
    onClose: () => void;

    open: boolean;
    level_name: string;
    level_topic: any

}> = (props) => {

    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">View Level</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        //gap: '1.5rem',
                    }}
                >
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Level Name</h2>
                            <p>{props.level_name}</p>
                        </IonLabel>
                    </IonItem>
                    <IonItem >
                        <IonLabel>
                            <h2 className="mb-3">Level Topics</h2>
                            {props.level_topic.map((item: any, i: any) =>
                                <p className="mt-3" key={i}> {i + 1}. {item}</p>
                            )}
                        </IonLabel>
                    </IonItem>


                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>

            </DialogActions>
        </Dialog>
    );
}
export default TchrLevel;
