import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';

import baseUrl, { api } from "../../pages/Urls";
import { Editor } from '@tinymce/tinymce-react';


const Articles: React.FC = () => {

    const [articles, setArticles] = useState([])
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);
    const [redirectToReferrer, setRedirectToReferrer] = React.useState(false);
    const [title, setTitle] = useState('');
    const [slug, setSlug] = useState('');
    const [content, setContent] = useState('');
    const [status, setStatus] = useState('');
    const [date, setDate] = useState('');
    const [id, setId] = useState('');
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        fetch(`${baseUrl}${api.allArticle}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticles(resp.reverse())
            })
    }, [])
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - articles.length) : 0;


    const editArticle = (id: any) => {

        fetch(`${baseUrl}${api.editArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setTitle(result.articles.title);
                setSlug(result.articles.slug);
                setContent(result.articles.content);
                setStatus(result.articles.status);
                setDate(result.articles.date);
                setId(result.articles.id)
            })
            .catch(function (error) {
                console.log(error);
            })


    }

    const updateArticle = () => {
        let data = {
            'title': title,
            'slug': slug,
            'content': content,
            'status': status,
            'date': date,
        }
        fetch(`${baseUrl}${api.updatetArticle}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => res.json())
            .then(result => {
                if (result) {
                    setRedirectToReferrer(true);
                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteArticle = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteArticle}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        setRedirectToReferrer(true);
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <div className="container mt-5">

            <div id="demo" className="collapse mt-3 mb-3">

                <>
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Article</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Name</IonLabel>
                                    <IonInput type="text"
                                        name="name"
                                        value={title}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setTitle(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Slug</IonLabel>
                                    <IonInput type="text"
                                        name="slug"
                                        value={slug}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setSlug(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked" >Content</IonLabel>
                                    <div style={{ marginTop: "3%" }}>
                                        <Editor
                                            textareaName="content"
                                            value={content}
                                            onEditorChange={(content: any, editor: any) => setContent(content)}
                                            init={{
                                                height: 500,
                                                width: 1030,
                                                menubar: true,
                                                plugins: [

                                                    'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                    'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                    'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                ],

                                                toolbar: 'undo redo | blocks | ' +
                                                    'bold italic forecolor | alignleft aligncenter ' +
                                                    'alignright alignjustify | bullist numlist outdent indent | ' +
                                                    'removeformat | help',
                                                content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                            }}
                                        />
                                    </div>

                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Status</IonLabel>
                                    <IonInput type="text"
                                        name="status"
                                        value={status}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setStatus(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Date</IonLabel>
                                    <IonInput type="datetime-local"
                                        name="date"
                                        value={date}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setDate(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>

                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="primary" onClick={updateArticle}>
                                        Update Group
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </>

            </div>

            <Paper sx={{ width: '100%', mb: 2 }}>

                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Id</TableCell>
                                <TableCell >Created By</TableCell>
                                <TableCell >Title</TableCell>
                                <TableCell >Slug</TableCell>
                                <TableCell >Status</TableCell>
                                <TableCell >Date</TableCell>
                                <TableCell >Action</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {(rowsPerPage > 0
                                ? articles.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                : articles
                            ).map((item: any, i: any) => (
                                <TableRow key={i.toString()}>
                                    <TableCell component="th" scope="row">{item.id}</TableCell>
                                    <TableCell >{item.user.name}</TableCell>
                                    <TableCell >{item.title}</TableCell>
                                    <TableCell >{item.slug}</TableCell>
                                    <TableCell style={{ color: (((item.status) == 'P') ? 'green' : 'red') }}>{item.status}</TableCell>
                                    <TableCell >{item.date}</TableCell>
                                    <TableCell >
                                        <button onClick={() => editArticle(item.id)} data-bs-toggle="collapse" data-bs-target="#demo">
                                            <i className="bi bi-pencil-square"></i>
                                        </button>

                                        <button onClick={() => deleteArticle(item.id)} >
                                            <i className="bi bi-trash"></i>
                                        </button>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>

                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[3, 5, 7]}
                    component="div"
                    count={articles.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </Paper>

        </div>
    );
}

export default Articles;
