import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,
    IonLabel, IonList, IonRadio, IonRadioGroup
} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewQuestion: React.FC = (props) => {
    type ViewQuestionParams = {
        id: string;
    };
    const [questions, setQuestions] = useState([]);
    const [question, setQuestion] = useState('');
    const [option, setOption]: any[] = useState([]);
    const [total_question, setTotalQuestion] = useState('');
    const [mark, setMark] = useState('');
    const [level_id, setLevel_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [duration, setDuration] = useState('');
    const [qIdx, setQIdx] = useState(0);
    const { id } = useParams<ViewQuestionParams>();
    const history = useHistory();
    const next = () => {

        if (qIdx + 1 < questions.length) {
            setQIdx(qIdx + 1);
        }
    };
    const previous = () => {

        if (qIdx + 1 < questions.length || qIdx + 1 == questions.length) {
            setQIdx(qIdx - 1);
        }
    };
    const submit = () => {


    };

    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setQuestions(result);
                setQuestion(result.map((op: any) => (op.question)))
                setTotalQuestion(result.length);
                setOption(result.map((op: any) => (op.answer)));
            })


            .catch(function (error) {
                console.log(error);
            })


    }, [])
    //console.log('Q', option);
    const Ref = useRef(null);

    // The state for our timer
    const [timer, setTimer] = useState('00:00:00');


    const getTimeRemaining = (e) => {
        const total = Date.parse(e) - Date.parse(new Date());
        const seconds = Math.floor((total / 1000) % 60);
        const minutes = Math.floor((total / 1000 / 60) % 60);
        const hours = Math.floor((total / 1000 / 60 / 60) % 24);
        return {
            total, hours, minutes, seconds
        };
    }


    const startTimer = (e) => {
        let { total, hours, minutes, seconds }
            = getTimeRemaining(e);
        if (total >= 0) {

            // update the timer
            // check if less than 10 then we need to 
            // add '0' at the beginning of the variable
            setTimer(
                (hours > 9 ? hours : '0' + hours) + ':' +
                (minutes > 9 ? minutes : '0' + minutes) + ':'
                + (seconds > 9 ? seconds : '0' + seconds)
            )
        }
    }


    const clearTimer = (e) => {

        // If you adjust it you should also need to
        // adjust the Endtime formula we are about
        // to code next    
        setTimer('00:00:10');

        // If you try to remove this line the 
        // updating of timer Variable will be
        // after 1000ms or 1sec
        if (Ref.current) clearInterval(Ref.current);
        const id = setInterval(() => {
            startTimer(e);
        }, 1000)
        Ref.current = id;
    }
    const getDeadTime = () => {
        let deadline = new Date();

        // This is where you need to adjust if 
        // you entend to add more time
        deadline.setSeconds(deadline.getSeconds() + 10);
        return deadline;
    }

    // We can use useEffect so that when the component
    // mount the timer will start as soon as possible

    // We put empty array to act as componentDid
    // mount only
    useEffect(() => {
        clearTimer(getDeadTime());
    }, []);
    ///////
    return (
        <>
            <IonContent>

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonGrid>
                                <IonRow>
                                    <IonCol>Question: {qIdx + 1} / {total_question}</IonCol>
                                    <IonCol> Time: </IonCol>
                                </IonRow>
                            </IonGrid>
                        </IonCardTitle>
                    </IonCardHeader>

                    <IonCardContent>
                        <>
                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>{question[qIdx]}</IonCardTitle>
                                </IonCardHeader>
                                <IonCardContent>


                                    <IonList>
                                        {option[qIdx] !== undefined ? option[qIdx].map((op: any, i: any) => {
                                            return (
                                                <IonRadioGroup key={i.toString()}>
                                                    <IonItem>
                                                        <IonLabel>{op.option}</IonLabel>
                                                        <IonRadio slot="start" value="dogs"></IonRadio>
                                                    </IonItem>

                                                </IonRadioGroup>
                                            );
                                        }) : undefined}
                                    </IonList>
                                    <div className='text-center mt-3 mb-2'>

                                        {(qIdx + 1 == 1) ? '' : <IonButton fill="outline" onClick={previous}>previous</IonButton>}
                                        {(qIdx + 1 == questions.length) ? '' : <IonButton fill="outline" onClick={next}>next</IonButton>}
                                        {(qIdx + 1 == questions.length) ? <IonButton fill="outline" onClick={submit}>submit</IonButton> : ''}
                                    </div>

                                </IonCardContent>

                            </IonCard>
                        </>
                    </IonCardContent>




                </IonCard>

            </IonContent>

        </>

    );
}
export default ViewQuestion;
