import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewLevel: React.FC = () => {
    type ViewLevelParams = {
        id: string;
    };
    const [quizlevel, setQuizlevel] = useState([]);
    const [quiz_name, setQuiz_name] = useState('');
    const [level_id, setLevel_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(1);
    const history = useHistory();
    const { id } = useParams<ViewLevelParams>();
    const back = () => {
        history.push("/dashboard/view/quiz")
    }

    useEffect(() => {

        fetch(`${baseUrl}${api.viewLevel}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                // setQuiz_name(result.quiz.quiz_name);
                //setQuiz_name(result.quiz_name);
                setQuizlevel(result.quizlevel);

            })
            .catch(function (error) {
                console.log(error);
            })

    }, [])


    return (
        <>
            <IonContent>

                <IonCard>
                    <IonCardHeader>
                        <IonCardSubtitle>

                            <IonButtons slot="start">
                                <IonButton onClick={back} > <i className="bi bi-arrow-left"></i> </IonButton>
                            </IonButtons>

                        </IonCardSubtitle>
                        <IonCardTitle>   <IonButtons slot="start">
                            <IonButton onClick={back} > <i className="bi bi-arrow-left"></i> </IonButton>
                        </IonButtons></IonCardTitle>

                    </IonCardHeader>

                    <IonCardContent>
                        Here's a small text description for the card content. Nothing more, nothing less.
                    </IonCardContent>
                </IonCard>
                {/* <IonToolbar>
                    <div className="text-center">
                        <IonTitle >
                            Levels
                        </IonTitle>
                    </div>
                    <IonButtons slot="start">
                        <IonButton onClick={back} > <i className="bi bi-arrow-left"></i> </IonButton>
                    </IonButtons>
                </IonToolbar> */}


            </IonContent>

        </>

    );
}
export default ViewLevel;
