import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';

import baseUrl, { api } from './Urls';


const Group: React.FC = () => {

    const [group, setGroup] = useState([])
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);
    const [redirectToReferrer, setRedirectToReferrer] = React.useState(false);
    const [name, setName] = useState('');

    useEffect(() => {
        fetch(`${baseUrl}${api.allGroup}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setGroup(resp)
            })
    }, [])
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    // import SearchBar from "material-ui-search-bar";
    // const [searched, setSearched] = React.useState("");
    // const requestSearch = (searchedVal: any) => {
    //     const filteredRows = data.filter((item: any) => {
    //         return item.name.toLowerCase().includes(searchedVal.toLowerCase());
    //     });
    //     setData(filteredRows);
    // };

    // const cancelSearch = () => {
    //     setSearched("");
    //     requestSearch(searched);
    // };

    // <SearchBar
    //       value={searched}
    //       onChange={(searchVal) => requestSearch(searchVal)}
    //       onCancelSearch={() => cancelSearch()}
    //     />
    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - group.length) : 0;

    const addGroup = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'name': name,
            }
            fetch(`${baseUrl}${api.addGroup}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        return response.json();
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })
                .then(result => {
                    if (result) {
                        setRedirectToReferrer(true);
                    }

                })
                .catch(error => console.log('error', error));

        }
    }
    const editGroup = (id: any) => {

        fetch(`${baseUrl}${api.editGroup}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setName(result.group.name);
            })
            .catch(function (error) {
                console.log(error);
            })


    }

    const updateGroup = (id: any) => {
        let data = {
            'name': name,
        }
        fetch(`${baseUrl}${api.updateGroup}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => res.json())
            .then(result => {
                if (result) {
                    setRedirectToReferrer(true);
                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteGroup = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteGroup}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        setRedirectToReferrer(true);
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <div className="container mt-5">
            <div className='mb-5'>
                <h2>Add Groups</h2>
                <div className="row">
                    <div className="col">
                        <input type="text"
                            className="form-control"
                            placeholder="enter name"
                            name="name" required
                            onChange={(e) => setName(e.target.value)} />
                    </div>
                    <div className="col">
                        <button type="button"
                            className="btn btn-light"
                            onClick={addGroup}
                        >
                            <i className="bi bi-plus-circle-fill"></i>
                        </button>
                    </div>
                </div>
            </div>

            <div id="demo" className="collapse mt-3 mb-3">

                <>
                    <div className="container mt-5 mb-5 ">


                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Group</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Name</IonLabel>
                                    <IonInput type="text"
                                        name="name"
                                        value={name}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setName(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>

                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="primary" onClick={updateGroup}>
                                        Update Group
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </>

            </div>

            <Paper sx={{ width: '100%', mb: 2 }}>

                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Id</TableCell>
                                <TableCell >Name</TableCell>
                                <TableCell >Action</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {(rowsPerPage > 0
                                ? group.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                : group
                            ).map((item: any, i: any) => (
                                <TableRow key={i.toString()}>
                                    <TableCell component="th" scope="row">{item.id}</TableCell>
                                    <TableCell >{item.name}</TableCell>
                                    <TableCell >
                                        <button onClick={() => editGroup(item.id)} data-bs-toggle="collapse" data-bs-target="#demo">
                                            <i className="bi bi-pencil-square"></i>
                                        </button>

                                        <button onClick={() => deleteGroup(item.id)} >
                                            <i className="bi bi-trash"></i>
                                        </button>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>

                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[5, 20, 30]}
                    component="div"
                    count={group.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </Paper>

        </div>
    );
}

export default Group;
