import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonDatetime,
    IonNote,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Collapse } from 'react-bootstrap'
import { useForm } from "react-hook-form";
const AddCourse: React.FC<{

    collapse: boolean
    instructor: any[]
    onClose: () => void;
    fetchCourse: () => void;

}> = (props) => {

    const [course_name, setCourse_name] = useState('');
    const [description, setDescription] = useState('');
    const [crs_inst, setCrs_inst] = useState([]);
    const {
        register,
        handleSubmit,
        formState: { errors }
    } = useForm();
    const [error, setError] = useState({
        course_name: '',
        crs_inst: ''
    });

    const addCourse = (e: any) => {
        {
            e.preventDefault();

            let data = {
                'course_name': course_name,
                'description': description,
                'crs_inst': crs_inst,
            }

            fetch(`${baseUrl}${api.addCourse}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {

                        props.onClose();
                        props.fetchCourse();
                        setCourse_name('');
                        setDescription('')
                        setCrs_inst([])
                    } else if (!course_name) {
                        setError({
                            course_name: 'Course name is required',
                            crs_inst: ''
                        })
                    }
                    else if (!crs_inst) {
                        setError({
                            course_name: '',
                            crs_inst: 'Course Instructor is required'
                        })
                    }

                    else {
                        throw new Error("Status code: " + response.status);
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    // console.log(error.course_name)

    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">
                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">Create Course</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>

                        <IonItem className={`${error.course_name && "ion-invalid"}`}>
                            <IonLabel position="stacked">Course Name</IonLabel>
                            <IonInput type="text"
                                name="course_name"
                                value={course_name}
                                required
                                clearInput
                                onIonChange={(e: any) => setCourse_name(e.target.value)}
                            >
                            </IonInput>
                            <IonNote slot="error">{error.course_name}</IonNote>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Description</IonLabel>
                            <IonInput type="text"
                                name="description"
                                value={description}
                                required
                                clearInput
                                onIonChange={(e: any) => setDescription(e.target.value)}
                            >
                            </IonInput>
                        </IonItem>

                        <IonItem className={`${error.crs_inst && "ion-invalid"}`}>
                            <IonLabel position="stacked">Course Instructor</IonLabel>
                            <IonSelect
                                name="crs_inst"
                                value={crs_inst}
                                onIonChange={(e: any) => setCrs_inst(e.target.value)}

                                placeholder="Select instructors" multiple={true}>
                                {props.instructor.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                ))}
                            </IonSelect>
                            <IonNote slot="error">{error.crs_inst}</IonNote>
                        </IonItem>


                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="danger" onClick={props.onClose}>
                                Cancel
                            </IonButton>
                            <IonButton color="primary" onClick={addCourse}>
                                Save
                            </IonButton>

                        </div>
                    </IonCardContent>
                </IonCard>

            </div>


        </Collapse>
    );
}

export default AddCourse;
