import { IonLabel, IonSegment, IonSegmentButton, IonContent, IonPage } from '@ionic/react';
import { Route } from 'react-router-dom';
import { useHistory } from "react-router";

const AdminNav: React.FC<{
    pathname: string
}> = (props) => {
    const history = useHistory()

    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }

    return (

        <IonSegment onIonChange={segmentChangeHandler} scrollable value={props.pathname}>
            <IonSegmentButton value="/dashboard/article">Article</IonSegmentButton>
            <IonSegmentButton value="/dashboard/group">Group</IonSegmentButton>
            <IonSegmentButton value="/dashboard/course">Course</IonSegmentButton>
            <IonSegmentButton value="/dashboard/topic">Topic</IonSegmentButton>
            <IonSegmentButton value="/dashboard/level">Level</IonSegmentButton>
            <IonSegmentButton value="/dashboard/quiz">Quiz</IonSegmentButton>
            <IonSegmentButton value="/dashboard/question">Question</IonSegmentButton>
            <IonSegmentButton value="/dashboard/trash">Trash</IonSegmentButton>
        </IonSegment>

    );
};

export default AdminNav;
