import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,

} from "@ionic/react";
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    FormControl,
    NativeSelect,
    Stack,
    Tooltip,
    InputLabel,
} from '@mui/material';
import Grid from '@material-ui/core/Grid';
import baseUrl, {api} from '../../../pages/Urls';


const Media: FC = () => {

    const [files, setFiles] = useState([]);

    const fetchData = () => {
        fetch(`${baseUrl}${api.media}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(res => {
                setFiles(res)
            });
    };

    useEffect(() => {
        fetchData();
    }, []);

    console.log('files', files);

    return (
        <>
            <IonContent>
                <div className="container mt-5">
                    <h1 className='text-center mb-2'>Media Gallery</h1>

                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        {files.map((file, i) => <Grid
                            key={`grid-file-${i}`}
                            item
                            xs={4}
                        >
                            item
                        </Grid>)}
                    </Box>
                </div>
            </IonContent>
        </>
    );
};

export default Media;
