import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
    InputLabel,
    FormControl,
    Select
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Typography } from '@mui/material';
import { IonItem, IonLabel } from '@ionic/react';
type levelData = {
    id: string;
    level_name: string;
    topic_name: string;


};

//nested data is ok, see accessorKeys in ColumnDef below


const TchrLevel: FC<{
    topic: any,
    level: any,
    course_name: string,
    fetchShowCourse: () => void
}> = (props) => {

    const [levelData, setLevelData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);


    const columns = useMemo<MRT_ColumnDef<levelData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                enableColumnOrdering: false,
                size: 80,
            },
            {
                accessorKey: 'topic_name',
                header: 'Topic Name',

            },
            {
                accessorKey: 'level_name',
                header: 'Level Name',

            },


        ],
        [],
    );

    const deleteLevel = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteLevel}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        props.fetchShowCourse();
                    }
                })
                .catch(error => console.log('error', error));
        }
    }
    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>
            <h1 className='text-center mb-2'>List of Levels</h1>
            <MaterialReactTable
                columns={columns}
                data={props.level}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                enableColumnOrdering
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="Edit">
                            <IconButton onClick={() => {
                                setLevelData(row.original)
                                setModalOpen(true);
                            }}>
                                <Edit />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteLevel(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add-Course">
                        <IconButton onClick={() => setModalOpen(true)}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}
            />
            <CreateOrEdit
                open={modalOpen}
                onClose={() => {
                    setModalOpen(false)
                    setLevelData({})


                }}
                fetchShowCourse={() => props.fetchShowCourse()}
                levelData={levelData}
                topic={props.topic}
            />
        </div>


    );

};

const CreateOrEdit: FC<{
    onClose: () => void;
    fetchShowCourse: () => void;
    open: boolean;
    levelData: any
    topic: any
}> = (props) => {

    const type = Object.keys(props.levelData).length == 0 ? "C" : "E";
    const formRef = useRef<HTMLFormElement>(null);
    const {
        register,
        watch,
        reset,
        handleSubmit,
        setValue,
        formState: { errors },
    } = useForm({
        criteriaMode: "all",

    });
    const addLevel = (e: any) => {
        {


            fetch(`${baseUrl}${api.addLevel}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Level Successfully');
                        props.fetchShowCourse();
                        props.onClose();
                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    const updateLevel = (e: any) => {

        fetch(`${baseUrl}${api.updateLevel}${e.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(e),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update level Successfully');
                    props.fetchShowCourse();
                    props.onClose();

                }

            })
            .catch(error => console.log('error', error));


    }
    const onSubmit = (e: any) => {
        type === "C" ? addLevel(e) : updateLevel(e);
    }
    useEffect(() => {

        {
            type === "E" ?
                reset({
                    id: props.levelData.id,
                    level_name: props.levelData.level_name,
                    //description: props.levelData.topic_name,

                }) : reset({
                    id: '',
                    level_name: '',

                })
        };
    }, [props.levelData]);

    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">{type === "C" ? "Create" : "Edit"} Level</DialogTitle>
            <DialogContent>
                <form ref={formRef} onSubmit={handleSubmit(onSubmit)}>
                    <Stack
                        sx={{
                            width: '100%',
                            minWidth: { xs: '300px', sm: '360px', md: '400px' },
                            gap: '1.5rem',
                        }}
                    >
                        {type === "C" ?
                            <>
                                <TextField sx={{ mt: 3 }}
                                    type="text"
                                    label="Level Name"
                                    variant="filled"
                                    focused
                                    required
                                    {...register("level_name")}
                                />
                                <FormControl sx={{ m: 1, minWidth: 120 }}>
                                    <InputLabel >Topic</InputLabel>
                                    <Select
                                        label="Topic"
                                        {...register("topic_name")}
                                        defaultValue={''}
                                    >

                                        {props.topic.map((item: any, i: any) => (
                                            < MenuItem key={i.toString()} value={item.topic_name}>{item.topic_name}</MenuItem>
                                        ))}

                                    </Select>

                                </FormControl>
                            </>
                            : <TextField sx={{ mt: 3 }}
                                type="text"
                                label="Level Name"
                                variant="filled"
                                focused
                                required
                                {...register("level_name")}
                            />
                        }


                    </Stack>
                    <DialogActions sx={{ p: '1.25rem' }}>
                        <Button onClick={props.onClose}>Cancel</Button>
                        <Button type="submit">{type === "C" ? "Create" : "Save"}</Button>
                    </DialogActions>
                </form>
            </DialogContent>

        </Dialog>
    );
}

export default TchrLevel;
