import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonText,
    IonGrid,
    IonRow,
    IonCol,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,
    IonLabel, IonList, IonRadio, IonRadioGroup, IonInput, IonCheckbox
} from '@ionic/react';

import React, { useState, useEffect, useRef } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
import { Link } from 'react-router-dom';
const ViewQuestion: React.FC = (props) => {
    type ViewQuestionParams = {
        id: string;
    };
    const [questions, setQuestions] = useState([]);
    const [total_question, setTotalQuestion] = useState('');
    const [minutes, setMinutes] = useState(1);
    const [seconds, setSeconds] = useState(0);

    const [attempt, setAttempt]: any[] = useState([]);
    const [question_id, setQuestion_id] = useState('');
    const [duration, setDuration] = useState(20);
    const { id } = useParams<ViewQuestionParams>();
    const history = useHistory();

    const submit = (e: any) => {
        setAttempt(question_id)
        if (window.confirm("want to submit?")) {

            e.preventDefault();
            let data = {
                // 'attempt_ans': isAns,
                'question_id': question_id,

            }
            fetch(`${baseUrl}${api.attemptQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {

                        window.location.href = '/dashboard/quiz';

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));


        }
    };
    const handleChange = (e: any, i: any) => {



    };
    const qId = (e: any) => {


        setQuestion_id(e.target.value),
            console.log(e.target.value)

    };


    useEffect(() => {

        fetch(`${baseUrl}${api.viewQuestion}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                setQuestions(result);
                //setQuestion_id(result.map((op: any) => (op.id)))
                setTotalQuestion(result.length);

            })


            .catch(function (error) {
                console.log(error);
            })


    }, [])
    //console.log('Q', isAns);

    useEffect(() => {
        let myInterval = setInterval(() => {
            if (seconds > 0) {
                setSeconds(seconds - 1);
            }
            if (seconds === 0) {
                if (minutes === 0) {
                    clearInterval(myInterval)
                } else {
                    setMinutes(minutes - 1);
                    setSeconds(59);
                }
            }
        }, 1000)
        return () => {
            clearInterval(myInterval);
        };
    });
    //console.log('op', question_id)

    return (

        <>
            <IonCard>
                <IonCardHeader>
                    <IonCardTitle>
                        <IonGrid>
                            <IonRow>
                                <IonCol>Total Question -- {total_question}</IonCol>
                                <IonCol>
                                    {minutes === 0 && seconds === 0
                                        ? ''
                                        : <h1> {minutes < 10 ? `0${minutes}` : minutes}:{seconds < 10 ? `0${seconds}` : seconds}</h1>
                                    }
                                </IonCol>
                            </IonRow>
                        </IonGrid>
                    </IonCardTitle>
                </IonCardHeader>

                <IonCardContent>

                    {questions.map((qstn: any, i: any) =>

                        <IonCard key={i.toString()}>
                            <IonCardHeader>
                                <IonCardTitle>
                                    {/* <div className='mb-2'>
                                        {++i} / {total_question}
                                    </div> */}

                                    {qstn.question}
                                    <input type='text'
                                        name='question_id'
                                        value={qstn.id}
                                        onChange={(e) => qId(e)}
                                    ></input>

                                </IonCardTitle>
                            </IonCardHeader>
                        </IonCard>


                    )}

                    <div className='text-center mt-3 mb-5'>
                        <IonButton fill="outline" onClick={(e) => submit(e)}>submit</IonButton>
                    </div>
                </IonCardContent>

            </IonCard>



        </>

    );
}
export default ViewQuestion;
