import {
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonContent,
    IonMenu,
    IonList,
    IonItem,
    IonRouterOutlet,
    IonMenuButton,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonSearchbar,

} from '@ionic/react';
import { Pagination } from '@mui/material';
import React, { useState } from 'react';
const Blog: React.FC = () => {
    const [article, setArticle] = useState([]);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(1);

    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - article.length) : 0;

    return (
        <>
            <div className="container mt-3">
                <h2>Recent Articles Post</h2>
                <hr />



                <div>
                    {(rowsPerPage > 0
                        ? article.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                        : article
                    )
                        .map((item: any, i: any) =>
                            <div className="card mb-3">
                                <div className="mt-3 mx-3" >

                                    <h4 className="text-primary" onClick={(e) => this.showModal(item.id)}>{item.title}</h4></div>

                                <>

                                    <IonModal isOpen={show} class="fullscreen">

                                        <IonHeader>
                                            <IonToolbar>
                                                <div className="text-center">
                                                    <IonTitle >{title}
                                                    </IonTitle>
                                                </div>
                                                <IonButtons slot="start">
                                                    <IonButton onClick={this.hideModal} > <i className="bi bi-backspace"> Back</i> </IonButton>
                                                </IonButtons>
                                            </IonToolbar>
                                        </IonHeader>

                                        <IonContent>
                                            <IonCardContent>
                                                <IonItem >
                                                    <h1 color="dark">{title}</h1>

                                                </IonItem>
                                                <IonItem>
                                                    <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>
                                                </IonItem>
                                                <IonItem>
                                                    <p>{date}</p>

                                                </IonItem>
                                            </IonCardContent>

                                        </IonContent>

                                    </IonModal>

                                </>
                                <div className="mt-2 mx-3" ><p>{item.date}</p></div>

                            </div>
                        )}

                    <div>
                        <Pagination


                            count={article.length}

                            page={page}
                        />
                    </div>
                </div>
            </div>

        </>

    );
}
export default Blog;
