import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';

import baseUrl, { api } from "../../pages/Urls";
import { Editor } from '@tinymce/tinymce-react';
import AddArticle from "./AddArticle";


const Articles: React.FC = () => {

    const [article, setArticle] = useState([]);
    const [group, setGroup] = useState([])
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(1);
    const [title, setTitle] = useState('');
    const [slug, setSlug] = useState('');
    const [content, setContent] = useState('');
    const [status, setStatus] = useState('');
    const [date, setDate] = useState('');
    const [id, setId] = useState('');
    const [name, setName] = useState('');


    useEffect(() => {
        fetch(`${baseUrl}${api.allArticle}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setArticle(resp.reverse())
            })
    }, [])
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - article.length) : 0;


    const editArticle = (id: any) => {

        fetch(`${baseUrl}${api.editArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setTitle(result.article.title);
                setSlug(result.article.slug);
                setContent(result.article.content);
                setStatus(result.article.status);
                setDate(result.article.date);
                setId(result.article.id)
                setName(result.group.name)

            })
            .catch(function (error) {
                console.log(error);
            })


    }

    const updateArticle = () => {
        let data = {
            'title': title,
            'slug': slug,
            'content': content,
            'status': status,
            'date': date,
            'name': name,
        }
        fetch(`${baseUrl}${api.updatetArticle}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })


            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Article Successfully');
                    window.location.href = '/dashboard';

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteArticle = (id: any) => {
        if (window.confirm('Move to trash?')) {
            fetch(`${baseUrl}${api.deleteArticle}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => {
                    if (res.status === 200) {
                        window.location.href = '/dashboard';
                    }
                })

                .catch(error => console.log('error', error));
        }
    }

    return (

        <>

            <div className="container mt-5">

                <div id="edit" className="collapse mt-3 mb-3">

                    <>
                        <div className="container mt-5 mb-5 ">

                            <IonCard>
                                <IonCardHeader>
                                    <IonCardTitle>
                                        <IonToolbar>
                                            <IonTitle><h1 className="text-center display-6">Edit Article</h1></IonTitle>
                                        </IonToolbar>
                                    </IonCardTitle>
                                </IonCardHeader>
                                <hr />
                                <IonCardContent>
                                    <IonItem>
                                        <IonLabel position="stacked">Title</IonLabel>
                                        <IonInput type="text"
                                            name="title"
                                            value={title}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setTitle(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Slug</IonLabel>
                                        <IonInput type="text"
                                            name="slug"
                                            value={slug}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setSlug(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked" >Content</IonLabel>
                                        <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                            <Editor
                                                textareaName="content"
                                                value={content}
                                                onEditorChange={(content: any, editor: any) => setContent(content)}
                                                init={{
                                                    height: 500,
                                                    width: 1030,
                                                    menubar: true,
                                                    plugins: [

                                                        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                    ],

                                                    toolbar: 'undo redo | blocks | ' +
                                                        'bold italic forecolor | alignleft aligncenter ' +
                                                        'alignright alignjustify | bullist numlist outdent indent | ' +
                                                        'removeformat | help',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                                }}
                                            />
                                        </div>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Group</IonLabel>
                                        <IonSelect
                                            name="gname"
                                            value={name}
                                            onIonChange={(e: any) => setName(e.target.value)}

                                            placeholder="Select groups" multiple={true}>
                                            {group.map((item: any, i: any) => (
                                                < IonSelectOption>{item.name}</IonSelectOption>
                                            ))}
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Status</IonLabel>

                                        <IonSelect
                                            name="status"
                                            value={status}
                                            onIonChange={(e: any) => setStatus(e.target.value)}
                                        >
                                            {(localStorage.getItem('role') == 'ar_mgmt') ?
                                                <>
                                                    <IonSelectOption>P</IonSelectOption>
                                                    <IonSelectOption>D</IonSelectOption>
                                                    <IonSelectOption>U</IonSelectOption>
                                                </>
                                                :
                                                <IonSelectOption>U</IonSelectOption>

                                            }
                                        </IonSelect>

                                    </IonItem>
                                    <IonItem>
                                        <IonLabel position="stacked">Date</IonLabel>
                                        <IonInput type="datetime-local"
                                            name="date"
                                            value={date}
                                            required
                                            clearInput
                                            onIonChange={(e: any) => setDate(e.target.value)}
                                        >
                                        </IonInput>
                                    </IonItem>

                                    <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                        <IonButton color="primary" onClick={updateArticle}>
                                            Update-Article
                                        </IonButton>

                                    </div>
                                </IonCardContent>
                            </IonCard>

                        </div>

                    </>

                </div>
                <AddArticle />
                <Paper sx={{ width: '100%', mb: 2 }}>
                    <p className="h1 text-center">List of Articles</p>
                    {(localStorage.getItem('role') == 'ar_staff2') ? '' :
                        <div className="float-end">
                            <button type="button"
                                className="btn btn-light mx-5 my-2"
                                data-bs-toggle="collapse" data-bs-target="#add"
                            >
                                <i className="bi bi-plus-circle-fill" title="add-article" ></i>
                            </button>

                        </div>
                    }
                    <TableContainer>
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                    <TableCell>Id</TableCell>
                                    <TableCell >Created By</TableCell>
                                    <TableCell >Title</TableCell>
                                    <TableCell >Slug</TableCell>
                                    <TableCell >Status</TableCell>
                                    <TableCell >Date</TableCell>
                                    <TableCell >Action</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {(rowsPerPage > 0
                                    ? article.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                    : article
                                ).map((item: any, i: any) => (
                                    <TableRow key={i.toString()}>
                                        <TableCell component="th" scope="row">{item.id}</TableCell>
                                        <TableCell >{item.user.name}</TableCell>
                                        <TableCell >{item.title}</TableCell>
                                        <TableCell >{item.slug}</TableCell>
                                        <TableCell >
                                            {
                                                (item.status === 'P' && 'Publish') ||
                                                (item.status === 'D' && 'Draft') ||
                                                (item.status === 'U' && 'Pending')
                                            }
                                        </TableCell>
                                        <TableCell >{item.date}</TableCell>
                                        {(localStorage.getItem('role') == 'ar_staff2') ? '' :
                                            <TableCell >

                                                <button onClick={() => editArticle(item.id)} data-bs-toggle="collapse" data-bs-target="#edit">
                                                    <i className="bi bi-pencil-square" title="edit-article"></i>
                                                </button>

                                                <button onClick={() => deleteArticle(item.id)} >
                                                    <i style={{ color: "green" }} className="bi bi-trash" title="move-to-trash"></i>
                                                </button>

                                            </TableCell>
                                        }
                                    </TableRow>
                                ))}
                            </TableBody>
                        </Table>

                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[1, 2, 5]}
                        component="div"
                        count={article.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onPageChange={handleChangePage}
                        onRowsPerPageChange={handleChangeRowsPerPage}
                    />
                </Paper>

            </div>
        </>
    );
}



export default Articles;
