import {
    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonText,
    IonAccordionGroup,
    IonAccordion,
} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import { useParams } from "react-router";
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import { Link } from 'react-router-dom';
import baseUrl, { api } from '../../pages/Urls';
import AddTopicArticle from './AddTopicArticle';
const ViewTopic: React.FC<{
    courseId: string

}> = (props) => {
    type ViewTopic = {
        id: string;
    };

    const [topic_name, setTopic_name] = useState('');
    const [topic_article, setTopic_article] = useState([]);
    const [topic_content, setTopic_content] = useState([]);
    const [tid, setId] = useState('');
    const [files, setFiles] = useState<any[]>([]);
    //const [content, setContent]: any[] = useState([]);
    const { id } = useParams<ViewTopic>();
    ///////////////////////////////////////
    useEffect(() => {

        fetch(`${baseUrl}${api.showTopic}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setTopic_name(result.topic.topic_name);
                setTopic_article(result.topic.topic_article);
                setTopic_content(result.topic.topic_content);
                localStorage.setItem('topic_name', result.topic.topic_name);
                localStorage.setItem('topic_id', result.topic.id);
                setId(result.topic.id)

            })
            .catch(function (error) {
                console.log(error);
            })


    }, [])
    const handleFiles = (e: any) => {
        //const filesArray: any[] = [];
        for (let i = 0; i < e.target.files.length; i++) {

            //filesArray.push(e.target.files[i]);
            setFiles(e.target.files[i]);

        }
        //setFiles(filesArray);
    };
    const addTopicFileTry = (e: any) => {

        var formdata = new FormData();
        formdata.append("topic_name", "English Topic");
        formdata.append("files[]", files[0], "document.pdf");


        fetch(`${baseUrl}${api.addTopicContent}`, {
            method: 'POST',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            },
            body: formdata,

        })
            .then(response => response.text())
            .then(result => console.log(result))
            .catch(error => console.log('error', error));

    }

    //const handleFiles = (e: any) => setFiles(e.target.files);
    //console.log(files);

    // const addTopicFile = (e: any) => {

    //     const formData = new FormData();
    //     formdata.append("files[]", fileInput.files[0], "photo-1472214103451-9374bd1c798e.jpeg");

    //     formData.append('topic_name', topic_name)
    //     fetch(`${baseUrl}${api.addTopicContent}`, {
    //         method: 'POST',
    //         headers: {
    //             Accept: 'application/json',
    //             'Content-Type': 'application/json',
    //             'Authorization': `Bearer ${localStorage.getItem("token")}`

    //         },
    //         body: formData,

    //     })

    // }

    // const addTopicFileTry = (e: any) => {
    //     var myHeaders = new Headers();
    //     myHeaders.append("Authorization", `Bearer ${localStorage.getItem("token")}`);


    //     var formdata = new FormData();
    //     // for (let i = 0; i < files.length; i++) {
    //     //     formdata.append("files[]", files[i], "any.pdf");
    //     // }
    //     // files.map((i: any) => {
    //     //     formdata.append("files[]", files[i], "any.pdf");
    //     // })
    //     formdata.append("files[]", files[0], "any2.pdf");
    //     //formdata.append("files[]", files.files[0], "quiz.jpeg");
    //     formdata.append("topic_name", "new update");

    //     var requestOptions = {
    //         method: 'POST',
    //         headers: myHeaders,
    //         body: formdata,

    //     };

    //     fetch(`${baseUrl}${api.addTopicContent}`, requestOptions)
    //         .then(response => response.json())
    //         .then(result => console.log(result))
    //         .catch(error => console.log('error', error));
    // }


    const updateTopic = () => {
        let data = {
            'topic_name': topic_name,

        }
        fetch(`${baseUrl}${api.updateTopic}${tid}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update Topic Successfully');
                    window.location.href = '/dashboard/topic';

                }

            })
            .catch(error => console.log('error', error));


    }

    const deleteTopicArticle = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteTopicArticle}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        window.location.href = `/dashboard/mycourse/${props.courseId}/topic/${tid}`;

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (
        <>
            <IonCard>
                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Update Topic</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">

                            <IonItem>
                                <IonLabel position="stacked">Topic Name</IonLabel>
                                <IonInput type="text"
                                    name="topic_name"
                                    value={topic_name}
                                    onIonChange={(e: any) => setTopic_name(e.target.value)}
                                    clearInput

                                >
                                </IonInput>
                            </IonItem>
                            <hr />
                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="primary" onClick={updateTopic}>
                                    Save
                                </IonButton>

                            </div>

                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>
            <IonCard >

                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Topic Content</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">
                            <input type="file" name="files[]" multiple
                                onChange={handleFiles} />
                            <IonButton color="primary" onClick={addTopicFileTry}>
                                Save
                            </IonButton>
                            {topic_content.map((item: any, i: any) => (
                                <IonItem key={i.toString()}>
                                    <IonLabel>{item.file}</IonLabel>
                                </IonItem>
                            ))}
                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>
            <IonCard >

                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Topic Article</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">
                            <div className="container mt-3">

                                <Paper sx={{ width: '100%', mb: 2 }}>
                                    <p className="h3 text-center">List of Topic Article</p>
                                    <div className="float-end mx-3">
                                        <Link className='text-decoration-none' to={`/dashboard/mycourse/${props.courseId}/topic/${tid}/add`}>
                                            <i className="bi bi-plus-circle-fill" title="add" ></i>
                                        </Link>

                                    </div>

                                    <TableContainer>

                                        <Table aria-label="simple table">
                                            <TableHead>
                                                <TableRow>
                                                    <TableCell>No</TableCell>
                                                    <TableCell >Article Name</TableCell>
                                                    <TableCell >Action</TableCell>

                                                </TableRow>
                                            </TableHead>
                                            <TableBody>
                                                {topic_article.map((item: any, i: any) => (
                                                    <TableRow key={i.toString()}>
                                                        <TableCell component="th" scope="row">{i + 1}</TableCell>
                                                        <TableCell >
                                                            {item.name}
                                                        </TableCell>

                                                        <TableCell >

                                                            <button >
                                                                <i className="bi bi-pencil-square" title="edit"></i>
                                                            </button>
                                                            <button onClick={() => deleteTopicArticle(item.id)}>
                                                                <i className="bi bi-trash" title="delete"></i>
                                                            </button>
                                                        </TableCell>
                                                    </TableRow>

                                                ))}

                                            </TableBody>
                                        </Table>

                                    </TableContainer>

                                </Paper>

                            </div>
                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>
        </>
    );
}
export default ViewTopic;
