import {
    IonCardHeader,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonButton,
    IonCardContent,
    IonCard,
    IonItem,
    IonContent

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import baseUrl, { api } from '../../../pages/Urls';

const ViewBlog: React.FC = () => {
    type ViewBlogParams = {
        id: string;
    };
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');
    const history = useHistory();
    const { id } = useParams<ViewBlogParams>();
    const back = () => {
        history.push("/dashboard/blog")
    }

    const viewArticle = () => {
        fetch(`${baseUrl}${api.viewArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setTitle(result.title);
                setContent(result.content);
                setDate(result.date);
            })
    }
    useEffect(() => {
        viewArticle();

    }, [])

    return (
        <IonContent>

            <IonCard>

                <IonCardHeader>
                    <IonToolbar>
                        <div className="text-center">
                            <IonTitle >
                                {title}
                            </IonTitle>
                        </div>
                        <IonButtons slot="start">
                            <IonButton onClick={back} > <i className="bi bi-backspace"> Back</i> </IonButton>
                        </IonButtons>
                    </IonToolbar>
                </IonCardHeader>


                <IonCardContent>
                    <IonItem>
                        <h1 color="dark">{title}</h1>

                    </IonItem>
                    <IonItem>
                        <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>
                    </IonItem>
                    <IonItem>
                        <p>{date}</p>

                    </IonItem>
                </IonCardContent>



            </IonCard>

        </IonContent>

    );
};
export default ViewBlog;
