import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Collapse } from 'react-bootstrap'
import { Editor } from '@tinymce/tinymce-react';
import { Typography } from '@mui/material';
import AddTopicArticle from './AddTopicArticle';
type articleData = {
    id: string;
    name: string;
};

//nested data is ok, see accessorKeys in ColumnDef below


const TopicArticle: FC<{
    fetchViewTopic: () => void;
    topicArticle: any
    topic_name: string
}> = (props) => {
    const [articleData, setArticleData] = useState({});
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const columns = useMemo<MRT_ColumnDef<articleData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                enableColumnOrdering: false,
                size: 80,
            },
            {
                accessorKey: 'name',
                header: 'Article Name',

            },


        ],
        [],
    );

    const deleteTopicArticle = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteTopicArticle}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        props.fetchViewTopic();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    //console.log(articleData)
    return (
        <>
            <div className="container mt-5">
                <UpdateArticle
                    collapse={collapse}
                    onClose={() => setCollapse(false)}
                    fetchViewTopic={() => props.fetchViewTopic()}
                    articleData={articleData}
                />
                <AddTopicArticle
                    open={open}
                    onClose={() => setOpen(false)}
                    fetchViewTopic={() => props.fetchViewTopic()}
                    topic_name={props.topic_name}

                />
                <h1 className='text-center mb-2'>List of Topic Articles</h1>
                <MaterialReactTable
                    columns={columns}
                    data={props.topicArticle}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton aria-expanded={collapse} onClick={() => {
                                    setArticleData(row.original)
                                    setCollapse(!collapse);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteTopicArticle(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-Article">
                            <IconButton aria-expanded={open} onClick={() => setOpen(!open)}>
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />

            </div>
        </>

    );


};

const UpdateArticle: FC<{
    onClose: () => void;
    fetchViewTopic: () => void;
    collapse: boolean;
    articleData: any
}> = (props) => {

    const [name, setName] = useState('');
    const [content, setContent] = useState('');
    const [id, setId] = useState('');

    useEffect(() => {
        setName(props.articleData.name);
        setContent(props.articleData.content);
        setId(props.articleData.id);

    }, [props.articleData]);
    const updateTopicArticle = () => {
        {
            let data = {
                'name': name,
                'content': content,

            }
            fetch(`${baseUrl}${api.updateTopicArticle}${id}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),

            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Update topic article Successfully');
                        props.fetchViewTopic();
                        props.onClose();

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }

    }
    return (
        <Collapse in={props.collapse}>

            <div>
                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">Update Topic Article</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>

                    <IonCardContent>


                        <IonItem>
                            <IonLabel position="stacked">Name</IonLabel>
                            <IonInput type="text"
                                name="name"
                                value={name}
                                placeholder="name..."
                                onIonChange={(e: any) => setName(e.target.value)}
                                clearInput></IonInput>
                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked" >Content</IonLabel>
                            <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                <Editor
                                    textareaName="content"
                                    value={content}
                                    onEditorChange={(content: any, editor: any) => setContent(content)}
                                    init={{
                                        height: 500,
                                        width: 1030,
                                        menubar: true,
                                        plugins: [

                                            'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                            'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                            'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                        ],

                                        toolbar: 'undo redo | blocks | ' +
                                            'bold italic forecolor | alignleft aligncenter ' +
                                            'alignright alignjustify | bullist numlist outdent indent | ' +
                                            'removeformat | help',
                                        content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                    }}
                                />
                            </div>

                        </IonItem>

                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="danger" onClick={props.onClose}>Cancel</IonButton>
                            <IonButton color="primary" onClick={updateTopicArticle}> Save</IonButton>

                        </div>

                    </IonCardContent>
                </IonCard>
            </div>

        </Collapse>
    );
}
export default TopicArticle;
