import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,

} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import TablePagination from '@mui/material/TablePagination';
import { Editor } from '@tinymce/tinymce-react';
import baseUrl, { api } from '../../../pages/Urls';



const Level: React.FC = () => {

    const [level, setLevel] = useState([]);
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [redirectToReferrer, setRedirectToReferrer] = React.useState(false);
    const [topic_id, setTopic_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [question, setQuestion] = useState('');
    const [q_content, setQContent] = useState('');
    const [questionType, setQuestionType] = useState('');
    const [answer, setAnswer] = useState('');
    const [option, setOption]: any[] = useState([]);
    ////////
    const [id, setId] = useState('');

    /////
    const handleAdd = () => {
        const abc = [...option, []]
        setOption(abc)
    }
    const handleChange = (onChangeValue: any, i: any) => {
        const inputdata = [...option]
        inputdata[i] = onChangeValue.target.value;
        setOption(inputdata)
    }
    const handleDelete = (i: any) => {
        const deletVal = [...option]
        deletVal.splice(i, 1)
        setOption(deletVal)
    }
    /////
    useEffect(() => {
        fetch(`${baseUrl}${api.allLevel}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setLevel(resp.reverse())
            })
    }, [])
    const handleChangePage = (event: any, newPage: any) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: any) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };


    const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - level.length) : 0;


    const editLevel = (id: any) => {

        fetch(`${baseUrl}${api.showLevel}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setLevel_name(result.level.level_name);
                setId(result.level.id)
            })
            .catch(function (error) {
                console.log(error);
            })

    }

    const updateLevel = () => {
        let data = {
            'level_name': level_name,

        }
        fetch(`${baseUrl}${api.updateLevel}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Update level Successfully');
                    window.location.href = 'dashboard/level';

                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteLevel = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteLevel}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(result => {
                    if (result) {
                        window.location.href = 'dashboard/level';
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    const addQuestion = (e: any) => {
        {
            e.preventDefault();
            let data = {
                'level_name': level_name,
                'title': question,
                'q_content': q_content,
                'question_type': questionType,
                'option': option,
                'answer': answer,

            }
            fetch(`${baseUrl}${api.addQuestion}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),

            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create Question Successfully');
                        window.location.href = 'dashboard/level';


                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }

    }
    //console.log('ccc', iscorrect)
    return (
        <div className="container mt-5">


            <div id="demo" className="collapse mt-3 mb-3">

                <>
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Level</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Level Name</IonLabel>
                                    <IonInput type="text"
                                        name="level_name"
                                        value={level_name}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setLevel_name(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>

                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="primary" onClick={updateLevel}>
                                        Update-Level
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </>

            </div>
            <div id="add" className="collapse mt-3 mb-3">
                <>
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">add Question</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Level Name</IonLabel>
                                    <IonInput type="text"
                                        name="level_name"
                                        value={level_name}
                                        disabled={true}
                                        onIonChange={(e: any) => setLevel_name(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Question Type</IonLabel>

                                    <IonSelect
                                        name="question_type"
                                        value={questionType}
                                        onIonChange={(e: any) => setQuestionType(e.target.value)}
                                    >

                                        <>
                                            <IonSelectOption>MCQ</IonSelectOption>
                                            <IonSelectOption>SQ</IonSelectOption>

                                        </>

                                    </IonSelect>

                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Question</IonLabel>
                                    <IonInput type="text"
                                        name="title"
                                        placeholder="question..."
                                        onIonChange={(e: any) => setQuestion(e.target.value)}
                                        clearInput></IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked" >Content</IonLabel>
                                    <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                        <Editor
                                            textareaName="content"
                                            value={q_content}
                                            onEditorChange={(content: any, editor: any) => setQContent(content)}
                                            init={{
                                                height: 500,
                                                width: 1030,
                                                menubar: true,
                                                plugins: [

                                                    'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                    'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                    'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                ],

                                                toolbar: 'undo redo | blocks | ' +
                                                    'bold italic forecolor | alignleft aligncenter ' +
                                                    'alignright alignjustify | bullist numlist outdent indent | ' +
                                                    'removeformat | help',
                                                content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'
                                            }}
                                        />
                                    </div>

                                </IonItem>
                                <IonText color="primary">
                                    <h2 className="mt-3 text-center">Add Question Option</h2>
                                </IonText>


                                <>
                                    <IonCard>
                                        <IonCardHeader>
                                            <IonCardTitle>
                                                <button onClick={() => handleAdd()}>
                                                    <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                </button>
                                            </IonCardTitle>

                                        </IonCardHeader>

                                        <IonCardContent>
                                            {option.map((op: any, i: any) => {
                                                return (
                                                    <div key={i.toString()} className="mt-2">

                                                        <input className="mx-2"
                                                            type="radio"
                                                            name="answer"
                                                            value={op}
                                                            onChange={(e: any) => setAnswer(e.target.value)}
                                                        />


                                                        <input type="text"
                                                            name="option[]"
                                                            placeholder="options..."
                                                            value={op} onChange={e => handleChange(e, i)} />
                                                        <button onClick={() => handleDelete(i)}>x</button>
                                                    </div>
                                                );
                                            })}
                                        </IonCardContent>


                                    </IonCard>
                                </>
                                <hr />
                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="primary" onClick={addQuestion}>
                                        Add Question
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </>

            </div>
            <Paper sx={{ width: '100%', mb: 2 }}>
                <p className="h1 text-center">List of levels</p>

                <TableContainer>
                    <Table aria-label="simple table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Id</TableCell>

                                <TableCell >Topic Name</TableCell>
                                <TableCell >Level Name</TableCell>
                                <TableCell >Action</TableCell>

                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {(rowsPerPage > 0
                                ? level.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                : level
                            ).map((item: any, i: any) => (
                                <TableRow key={i.toString()}>
                                    <TableCell component="th" scope="row">{item.id}</TableCell>

                                    <TableCell >{item.topic_name}</TableCell>
                                    <TableCell >{item.level_name}</TableCell>

                                    <TableCell >
                                        <button onClick={() => editLevel(item.id)} data-bs-toggle="collapse" data-bs-target="#add">
                                            <i className="bi bi-plus-circle-dotted" title="add-question"></i>
                                        </button>
                                        <button onClick={() => editLevel(item.id)} data-bs-toggle="collapse" data-bs-target="#demo">
                                            <i className="bi bi-pencil-square" title="edit-quiz-level"></i>
                                        </button>

                                        <button onClick={() => deleteLevel(item.id)} >
                                            <i className="bi bi-trash" title="delete"></i>
                                        </button>
                                    </TableCell>
                                </TableRow>
                            ))}


                        </TableBody>
                    </Table>

                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[10, 15, 20]}
                    component="div"
                    count={level.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </Paper>

        </div >
    );
}

export default Level;
