import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import { format } from "date-fns";
import swal from 'sweetalert';
import { useForm, Controller } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { TextField } from "@mui/material";
import { DesktopDatePicker, LocalizationProvider } from "@mui/x-date-pickers";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
const schema = yup.object().shape({
    email: yup.string()
        .required('Email is required')
        .test('Unique Email', 'Email already in use', // <- key, message

            function (value) {
                if (value) {
                    return new Promise((resolve, reject) => {

                        fetch(`${baseUrl}${api.checkUser}${value}`, {
                            method: 'GET',
                            headers: {
                                Accept: 'application/json',
                                'Content-Type': 'application/json',

                            },
                        })
                            .then((res) => {
                                resolve(false)
                            })
                            .catch((error) => {

                                resolve(true);

                            })


                    })
                }

            }
        ),
    name: yup.string()
        .required('Name is required'),
    password: yup.string()
        .required('Password is required')
})
const AddArticle: React.FC<{

    collapse: boolean
    group: any[]
    onClose: () => void;
    fetchArticle: () => void;

}> = (props) => {

    const {
        handleSubmit,
        register,
        formState: { errors },
        reset,
        control,
        setValue,
    } = useForm({
        defaultValues: {
            title: '',
            slug: '',
            content: '',
            status: '',
            gname: [],
            date: format(new Date(), "yyyy-MM-dd"),

        }
    });
    const addArticle = (e: any) => {
        {


            fetch(`${baseUrl}${api.createArticle}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchArticle();
                        props.onClose();
                        reset({
                            title: '',
                            slug: '',
                            content: '',
                            status: '',
                            gname: [],

                        })
                        swal({
                            title: "Success!",
                            text: "Article Created successfully",
                            icon: "success",
                        });
                    }


                })

                .catch(errors => console.log(errors.response.data.errors));
        }
    }


    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">

                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">Add Article</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>
                        <form onSubmit={handleSubmit(addArticle)}>
                            <IonItem>
                                <IonLabel position="stacked">Title</IonLabel>
                                <IonInput placeholder="Title"
                                    type="text"
                                    {...register("title", { required: "Title Is Required" })}
                                >
                                </IonInput>
                            </IonItem>
                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name="title" />
                            </p>
                            <IonItem>
                                <IonLabel position="stacked">Slug</IonLabel>
                                <IonInput placeholder="slug"
                                    type="text"
                                    {...register("slug", { required: "Slug Is Required" })}
                                >
                                </IonInput>
                            </IonItem>
                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name="slug" />
                            </p>
                            <IonItem>
                                <IonLabel position="stacked" >Content</IonLabel>
                                <div style={{ marginTop: "3%", marginBottom: "3%" }}>
                                    <Controller
                                        name="content"
                                        control={control}
                                        rules={{
                                            required: 'Content is required',
                                        }}
                                        render={({ field: { ...rest } }) => (
                                            <Editor
                                                textareaName="content"
                                                value={rest.value}

                                                init={{
                                                    height: 500,
                                                    width: 1030,
                                                    menubar: true,
                                                    plugins: [

                                                        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
                                                        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
                                                        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'

                                                    ],

                                                    toolbar: 'undo redo | blocks | ' +
                                                        'bold italic forecolor | alignleft aligncenter ' +
                                                        'alignright alignjustify | bullist numlist outdent indent | ' +
                                                        'removeformat | help',
                                                    content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }'

                                                }}
                                                onEditorChange={(e) => {
                                                    setValue('content', e)
                                                }}
                                            />

                                        )}
                                    />
                                </div>

                            </IonItem>
                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name="content" />
                            </p>
                            <IonItem>
                                <IonLabel position="stacked">Group</IonLabel>
                                <IonSelect
                                    {...register("gname")}

                                    placeholder="Select groups" multiple={true}>
                                    {props.group.map((item: any, i: any) => (
                                        < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                    ))}
                                </IonSelect>

                            </IonItem>
                            <IonItem>
                                <IonLabel position="stacked">Status</IonLabel>

                                <IonSelect
                                    placeholder="status"

                                    {...register("status", { required: "Status Is Required" })}
                                >
                                    {(localStorage.getItem('role') == 'ar_staff1') ?
                                        <IonSelectOption value='R'>Review</IonSelectOption> :
                                        <>
                                            <IonSelectOption value='P'>Publish</IonSelectOption>
                                            <IonSelectOption value='D'>Draft</IonSelectOption>

                                        </>



                                    }
                                </IonSelect>

                            </IonItem>
                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name="status" />
                            </p>

                            <IonItem>
                                <IonLabel position="stacked">Date</IonLabel>

                                <Controller
                                    name="date"
                                    control={control}
                                    rules={{
                                        required: 'Date is required',
                                    }}
                                    defaultValue={format(new Date(), "yyyy-MM-dd")}
                                    render={({ field: { ...rest } }) => (
                                        <LocalizationProvider dateAdapter={AdapterDateFns}>
                                            <DesktopDatePicker

                                                inputFormat="yyyy-MM-dd"

                                                value={rest.value}
                                                onChange={(e: any) => {
                                                    setValue("date", format(e, "yyyy-MM-dd"));

                                                }}
                                                renderInput={(params) => (
                                                    <TextField {...params} sx={{ mt: 1, width: '100%' }} />
                                                )}
                                            />
                                        </LocalizationProvider>
                                    )}
                                />

                            </IonItem>

                            <p style={{ color: "red" }}>
                                <ErrorMessage errors={errors} name="date" />
                            </p>
                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="danger" onClick={props.onClose}>
                                    Cancel
                                </IonButton>
                                <IonButton color="primary" type="submit">
                                    Create
                                </IonButton>

                            </div>
                        </form>
                    </IonCardContent>
                </IonCard>

            </div>


        </Collapse >
    );
}

export default AddArticle;
