import { IonLabel, IonSegment, IonSegmentButton, IonContent } from '@ionic/react';
import { Route } from 'react-router-dom';
import { useHistory } from "react-router";

const UserNav: React.FC<{
    pathname: string
}> = (props) => {
    const history = useHistory()

    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }

    return (
        <IonSegment onIonChange={segmentChangeHandler} scrollable value={props.pathname}>
            <IonSegmentButton value="/dashboard/blog">blog</IonSegmentButton>
            <IonSegmentButton value="/dashboard/quiz">quiz</IonSegmentButton>
        </IonSegment>

    );
};

export default UserNav;
