    IonInput,

    IonMenuButton,
    IonPage,
    IonRouterOutlet,
    IonRow,
    IonSplitPane,
    IonCard,
    IonCardTitle,
    IonAccordion,
    IonAccordionGroup,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import { Redirect, Route, useLocation } from "react-router-dom";
import Course from '../admin/quiz/Course';
import baseUrl, { api } from '../../pages/Urls';
const ViewCourse: React.FC = () => {
    type ViewCourse = {
        id: string;
    };
    const history = useHistory()
    const location = useLocation()
    const { id } = useParams<ViewCourse>();
    const [course_name, setCourse_name] = useState('');
    const [quiz, setQuiz] = useState([]);
    const [topic, setTopic] = useState([]);
    const [topic_name, setTopic_name] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    useEffect(() => {
        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setCourse_name(result.course.course_name)
                setQuiz(result.course.quiz)
                setTopic(result.course.topic)
            })

    }, []);

    return (

        <IonPage>
            <IonHeader>
                <IonToolbar>
                    <IonTitle>PAGE ONE</IonTitle>
                    <IonButtons slot="end">

                    </IonButtons>
                </IonToolbar>
            </IonHeader>
            <IonContent fullscreen>
            </IonContent>
        </IonPage>

    );


}
export default ViewCourse;
