import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    IconButton,
    Tooltip,
} from '@mui/material';
import { Input } from '@mui/icons-material';
import baseUrl, { api } from '../../../pages/Urls';
import { IonContent } from '@ionic/react';
type CourseData = {
    id: string;
    course_name: string;
    description: string;
    is_enrole: any;

};

//nested data is ok, see accessorKeys in ColumnDef below


const PendingEnrole: FC = () => {
    const [course, setCourse] = useState([]);
    const [courseData, setCourseData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);

    const fetchCourse = () => {
        fetch(`${baseUrl}${api.pendingEnrole}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(resp => {
                setCourse(resp.reverse())
            })
    }
    useEffect(() => {
        fetchCourse();

    }, [])

    const columns = useMemo<MRT_ColumnDef<CourseData>[]>(
        () => [


            {
                accessorKey: 'course_name',
                header: 'Course Name',

            },
            {
                accessorKey: 'description',
                header: 'Description',

            },
            {
                accessorKey: 'is_enrole',
                header: 'Status',
                Cell: ({ cell }) => (
                    cell.getValue<any>() === 0 && 'Pending'

                ),
            },

        ],
        [],
    );


    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>Pending Enrole Courses</h1>
                <MaterialReactTable
                    columns={columns}
                    data={course}
                    getRowId={(row => `${row.id}`)}
                    enableRowNumbers
                    enableColumnOrdering

                />

            </div>
        </IonContent>

    );

};

export default PendingEnrole;
