import {
    IonMenu,
    IonHeader,
    IonToolbar,
    IonTitle,
    IonContent,
    IonList,
    IonListHeader,
    IonNote,
    IonMenuToggle,
    IonItem,
    IonLabel,
    IonButton,
    IonButtons,
    IonCol,

    IonGrid,

    IonInput,

    IonMenuButton,
    IonPage,
    IonRouterOutlet,
    IonRow,
    IonSplitPane,
    IonCard,
    IonCardTitle,
    IonAccordion,
    IonAccordionGroup,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import { Redirect, Route, useLocation } from "react-router-dom";
import Course from '../admin/quiz/Course';
import baseUrl, { api } from '../../pages/Urls';
const ViewCourse: React.FC = () => {
    type ViewCourse = {
        id: string;
    };
    const history = useHistory()
    const location = useLocation()
    const { id } = useParams<ViewCourse>();
    const [course_name, setCourse_name] = useState('');
    const [quiz, setQuiz] = useState([]);
    const [topic, setTopic] = useState([]);
    const [topic_name, setTopic_name] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    useEffect(() => {
        fetch(`${baseUrl}${api.showCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setCourse_name(result.course.course_name)
                setQuiz(result.course.quiz)
                setTopic(result.course.topic)
            })

    }, []);
    const addTopic = (e: any) => {
        {
            let data = {
                'course_name': course_name,
                'topic_name': topic_name,

            }
            fetch(`${baseUrl}${api.addTopic}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(data),
            })

                .then(response => {
                    if (response.status === 200) {
                        window.alert('Create topic Successfully');
                        window.location.href = '/dashboard/course';

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (

        <>
            <IonSplitPane when='sm' contentId="main-content">
                <IonMenu contentId="main-content">

                    <IonContent className="ion-padding">
                        <IonList>
                            <IonListHeader >
                                <IonItem routerLink="/dashboard/view/course/:id" routerDirection="none">
                                    <IonLabel> Course Name {course_name}</IonLabel>
                                </IonItem>

                            </IonListHeader>

                            <IonMenuToggle autoHide={false}>
                                <IonItem >
                                    <IonAccordionGroup>
                                        <IonAccordion value="first">
                                            <IonItem slot="header" color="light">
                                                <IonLabel>Topic</IonLabel>
                                            </IonItem>
                                            <div className="ion-padding" slot="content">
                                                <IonItem>
                                                    <IonLabel position="stacked">Topic Name</IonLabel>
                                                    <IonInput type="text"
                                                        name="topic_name"
                                                        placeholder="topic name..."
                                                        onIonChange={(e: any) => setTopic_name(e.target.value)}
                                                        clearInput></IonInput>
                                                </IonItem>

                                                <IonButton color="primary" onClick={addTopic}>
                                                    Add
                                                </IonButton>


                                            </div>
                                        </IonAccordion>
                                    </IonAccordionGroup>
                                </IonItem>
                                <IonItem className={location.pathname === '/dashboard/view/course/:id/quiz' ? "selected" : ''} routerLink="/dashboard/view/course/:id/quiz" routerDirection="none">
                                    <IonLabel>Quiz</IonLabel>
                                </IonItem>
                                <IonItem className={location.pathname === '/dashboard/view/course/:id/level' ? "selected" : ''} routerLink="/dashboard/view/course/:id/level" routerDirection="none">
                                    <IonLabel>Level</IonLabel>
                                </IonItem>
                                <IonItem className={location.pathname === '/dashboard/view/course/:id/question' ? "selected" : ''} routerLink="/dashboard/view/course/:id/question" routerDirection="none">
                                    <IonLabel>Question</IonLabel>
                                </IonItem>
                                <IonItem className={location.pathname === '/dashboard/view/course/:id/quizattempt' ? "selected" : ''} routerLink="/dashboard/view/course/:id/quizattempt" routerDirection="none">
                                    <IonLabel>Quiz Attempt</IonLabel>
                                </IonItem>
                            </IonMenuToggle>
                        </IonList>
                        <IonList>
                            <IonListHeader>My Course List</IonListHeader>
                            <IonItem routerLink="/dashboard/my/course" routerDirection="none">
                                <IonLabel>My Course</IonLabel>
                            </IonItem>
                        </IonList>

                    </IonContent>
                </IonMenu>
                <div className="ion-page" id="main-content">
                    <TemporaryHeader />

                    <IonContent className="ion-padding">
                        <List quiz={quiz} topic={topic} />
                        <IonRouterOutlet id="main-content">
                            {/* <Route path="/dashboard/view/course/:id" exact={true}>
                                <List quiz={quiz} topic={topic} />
                            </Route> */}

                            <Route path="/dashboard/view/course/:id/quiz" exact component={Course} />
                            <Route path="/dashboard/view/course/:id/level" exact component={Course} />
                            <Route path="/dashboard/view/course/:id/question" exact component={Course} />
                            <Route path="/dashboard/view/course/:id/quizattempt" exact component={Course} />

                            <Route path="/dashboard" exact>
                                <Redirect to="/dashboard/view/course/:id" />
                            </Route>

                        </IonRouterOutlet>
                    </IonContent>
                </div>

            </IonSplitPane>
        </>


    );


}

const TemporaryHeader: React.FC = () => {
    const location = useLocation();
    const title = () => {
        if (location.pathname === "/dashboard/view/course/:id/quiz") return "Quiz";
        if (location.pathname === "/dashboard/view/course/:id/level") return "Level";
        if (location.pathname === "/dashboard/view/course/:id/question") return "Question";
        if (location.pathname === "/dashboard/view/course/:id") return "Course";

    };
    return (
        <IonHeader>
            <IonToolbar>
