import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonDatetime,
    IonNote,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Collapse } from 'react-bootstrap'
import { useForm, Controller } from "react-hook-form";
import {
    TextField,
    Box,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Checkbox,
    ListItemText,
    OutlinedInput,
    Stack,
    Card,
    CardContent,
} from "@mui/material";
const AddCourse: React.FC<{

    collapse: boolean
    instructor: any[]
    onClose: () => void;
    fetchCourse: () => void;

}> = (props) => {


    const {
        handleSubmit,
        control,
        setValue,
        register,
        getValues,
        formState: { errors }
    } = useForm({
        defaultValues: {
            course_name: '',
            description: '',
            crs_inst: [],

        }
    });


    const addCourse = (e: any) => {
        {
            e.preventDefault();



            fetch(`${baseUrl}${api.addCourse}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {

                        props.onClose();
                        props.fetchCourse();

                    }

                    else {
                        throw new Error("Status code: " + response.status);
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    // console.log(error.course_name)

    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">
                <Card>

                    <CardContent>

                        <Box
                            component="form"
                            sx={{
                                '& .MuiTextField-root': { m: 1, width: '100ch' },
                            }}
                            noValidate
                            autoComplete="off"
                        >


                            <TextField
                                required
                                id="filled-required"
                                label="Course Name"
                                variant="filled"
                            />


                        </Box>

                    </CardContent>
                </Card>

            </div>


        </Collapse >
    );
}

export default AddCourse;
