import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonDatetime,
    IonNote,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Collapse } from 'react-bootstrap'
import { useForm, Controller } from "react-hook-form";
import {
    TextField,
    Box,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Checkbox,
    ListItemText,
    OutlinedInput,
    Typography,
    Card,
    CardContent,
    CardHeader,
} from "@mui/material";
const AddCourse: React.FC<{

    collapse: boolean
    instructor: any[]
    onClose: () => void;
    fetchCourse: () => void;

}> = (props) => {


    const {
        handleSubmit,
        control,
        setValue,
        register,
        getValues,
        formState,
        reset,

    } = useForm({
        defaultValues: {
            course_name: '',
            description: '',
            crs_inst: [],

        }
    });


    const addCourse = (e: any) => {
        {

            fetch(`${baseUrl}${api.addCourse}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {

                        props.onClose();
                        props.fetchCourse();

                    }

                    else {
                        throw new Error("Status code: " + response.status);
                    }
                })

                .catch(error => console.log('error', error));

        }
    }

    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">
                <IonCard>
                    <IonCardHeader>
                        <IonCardTitle>
                            <IonToolbar>
                                <IonTitle><h1 className="text-center display-6">Create Course</h1></IonTitle>
                            </IonToolbar>
                        </IonCardTitle>
                    </IonCardHeader>
                    <hr />
                    <IonCardContent>

                        <IonItem>
                            <IonLabel position="stacked">Course Name</IonLabel>

                            <Controller
                                as={IonInput}
                                control={control}
                                onChangeName="onIonChange"
                                onChange={([selected]) => {
                                    console.log("fullName", selected.detail.value);
                                    return selected.detail.value;
                                }}
                                name="course_name"
                                rules={{
                                    required: true,
                                    minLength: { value: 4, message: "Must be 4 chars long" }
                                }}
                            />


                        </IonItem>
                        <IonItem>
                            <IonLabel position="stacked">Description</IonLabel>
                            <IonInput type="text"
                                name="description"
                                value={description}
                                required
                                clearInput
                                onIonChange={(e: any) => setDescription(e.target.value)}
                            >
                            </IonInput>
                        </IonItem>

                        <IonItem className={`${error.crs_inst && "ion-invalid"}`}>
                            <IonLabel position="stacked">Course Instructor</IonLabel>
                            <IonSelect
                                name="crs_inst"
                                value={crs_inst}
                                onIonChange={(e: any) => setCrs_inst(e.target.value)}

                                placeholder="Select instructors" multiple={true}>
                                {props.instructor.map((item: any, i: any) => (
                                    < IonSelectOption key={i.toString()}>{item.name}</IonSelectOption>
                                ))}
                            </IonSelect>
                            <IonNote slot="error">{error.crs_inst}</IonNote>
                        </IonItem>


                        <div style={{ marginTop: "3%" }} className="text-center mb-5">
                            <IonButton color="danger" onClick={props.onClose}>
                                Cancel
                            </IonButton>
                            <IonButton color="primary" onClick={addCourse}>
                                Save
                            </IonButton>

                        </div>
                    </IonCardContent>
                </IonCard>

            </div>


        </Collapse>
    );
}

export default AddCourse;
