import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
    IonPopover,
    IonDatetime,

} from "@ionic/react";
import React, { FC, useMemo, useState } from 'react';
import MaterialReactTable, {
    MRT_ColumnDef,

} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    Stack,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add, Visibility } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Collapse } from 'react-bootstrap'
import AddQuiz from './AddQuiz';
import { format } from 'date-fns';
import swal from 'sweetalert';
import Select from 'react-select';
import { useForm, Controller, useFieldArray } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import { AdapterDateFns } from "@mui/x-date-pickers/AdapterDateFns";
import { TextField } from "@mui/material";
import { DateTimePicker, LocalizationProvider } from "@mui/x-date-pickers";
type quizData = {
    id: string;
    start_date: Date;
    quiz_name: string;
    status: string;
    test_time: any;

};

const schema = yup.object().shape({
    quiz_name: yup.string()
        .required('Quiz Name is required'),
    test_time: yup.string()
        .required('Quiz duration is required'),
    status: yup.string()
        .required('Quiz Status is required'),
    start_date: yup.string()
        .required('Quiz Start date is required'),
    inputlist: yup.array().required().min(1),

})
const TchrQuiz: FC<{

    quiz: any
    level: any
    course_name: string
    fetchShowCourse: () => void
}> = (props) => {
    const [quizId, setQuizId] = useState('');
    const type = quizId ? "E" : "C";
    const {
        handleSubmit,
        register,
        formState: { errors },
        reset,
        control,
        setValue,
    } = useForm({
        defaultValues: {
            id: '',
            course_name: props.course_name,
            quiz_name: '',
            test_time: '',
            status: '',
            inputlist: [{ level_name: '', qcount: '' }],
            start_date: format(new Date(), "yyyy-MM-dd HH:mm"),

        }
    });
    const { fields, remove, append } = useFieldArray({
        name: 'inputlist',
        control,
    });
    const [quiz_name, setQuiz_name] = useState('');

    const [status, setStatus] = useState('');
    const [dateTimeValue, setDateTimeValue] = useState<any>(
        format(new Date(), "yyyy-MM-dd HH:mm")
    );
    const [duration, setDuration] = useState('');
    const [level, setLevel] = useState([]);
    const [error, setError] = useState("");
    const [showQuiz, setShowQuiz] = useState([]);
    const [quizData, setQuizData] = useState({});
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const [modalOpen, setModalOpen] = useState(false);
    const [inputList, setinputList] = useState([{ level_name: '', qcount: '' }]);
    const handleinputchange = (e: any, index: any) => {
        const { name, value } = e.target;
        const list: any[] = [...inputList];
        list[index][name] = value;
        setinputList(list);


    }
    const handleaddclick = () => {

        const abc = [...inputList, { level_name: '', qcount: '' }]

        setinputList(abc);
    }
    const handleremove = (index: any) => {
        const list = [...inputList];
        list.splice(index, 1);
        setinputList(list);
    }


    const columns = useMemo<MRT_ColumnDef<quizData>[]>(
        () => [



            {
                accessorKey: 'quiz_name',
                header: 'Quiz Name',

            },
            {
                accessorKey: 'status',
                header: 'Status',
                Cell: ({ cell }) => (
                    cell.getValue<string>() === "P" ?
                        'Publish' : 'Draft'
                ),
            },
            {
                accessorKey: 'start_date',
                header: 'Start Date',

            },
            {
                accessorKey: 'test_time',
                header: 'Duration (Minutes)',

            },


        ],
        [],
    );



    const editQuiz = (id: any) => {
        setQuizId(id);
        fetch(`${baseUrl}${api.showQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setValue('quiz_name', result.quiz.quiz_name);
                setValue('status', result.quiz.status);
                setValue('start_date', result.quiz.start_date);
                setValue('test_time', result.quiz.test_time);
                setValue('id', result.quiz.id)
                setLevel(result.level);
                setShowQuiz(result.quiz.qlevel)
                let levelList: any[] = [];
                {
                    result.quiz.qlevel.map((item: any, i: any) =>
                        levelList.push({
                            level_name: item.level_name,
                            qcount: item.qcount

                        })
                    )
                }
                setValue('inputlist', [...levelList]);

            })
            .catch(function (error) {
                console.log(error);
            })


    }

    const addQuiz = (e: any) => {
        {

            fetch(`${baseUrl}${api.addQuiz}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchShowCourse();
                        setCollapse(false)

                        swal({
                            title: "Success!",
                            text: "Quiz Created successfully",
                            icon: "success",
                        });
                    }
                    else {

                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    const updateQuiz = (e: any) => {

        fetch(`${baseUrl}${api.updateQuiz}${e.id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(e),
        })

            .then(res => {
                if (res.status === 200) {
                    props.fetchShowCourse();
                    setOpen(false)
                    setinputList([]);
                    swal({
                        title: "Success!",
                        text: "Quiz updated successfully",
                        icon: "success",
                    });
                } else {
                    swal({
                        title: "Error!",
                        text: res.status + ' ' + res.statusText +
                            " - Quiz not updated",
                        icon: "error",
                    });
                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteQuiz = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuiz}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })
                .then(res => {
                    if (res.status === 200) {

                        props.fetchShowCourse();
                        swal({
                            title: "Success!",
                            text: "Quiz deleted successfully",
                            icon: "success",
                        });
                    } else {
                        swal({
                            title: "Error!",
                            text: res.status + ' ' + res.statusText +
                                " - Quiz not deleted",
                            icon: "error",
                        });
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    const onSubmit = (e: any) => {
        type === "C" ? addQuiz(e) : updateQuiz(e);
    }
    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>
            <Collapse in={collapse}>
                <div id="collapseID">
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">{type === "C" ? "Create" : "Edit"} Quiz</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Quiz Name</IonLabel>
                                    <IonInput type="text"
                                        name="quiz_name"
                                        value={quiz_name}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setQuiz_name(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonText color="primary">
                                        <h2 className="mt-3 mb-3">Add Quiz Level</h2>
                                    </IonText>

                                </IonItem>
                                <div>

                                    {
                                        inputList.map((x, i) => {

                                            return (
                                                <div key={i.toString()} className="row mb-3 mt-3">
                                                    <div className=" col-md-4">
                                                        <IonItem>
                                                            <IonLabel position="stacked">Level</IonLabel>
                                                            <IonSelect
                                                                name="level_name"
                                                                value={x.level_name}
                                                                onIonChange={(e: any) => handleinputchange(e, i)}

                                                            >
                                                                {level.map((item: any, index: any) => (
                                                                    < IonSelectOption key={index.toString()}>{item.level_name}</IonSelectOption>
                                                                ))}
                                                            </IonSelect>
                                                        </IonItem>
                                                    </div>
                                                    <div className=" col-md-4">
                                                        <IonItem>
                                                            <IonLabel position="stacked">No of Question</IonLabel>
                                                            <IonInput type="number"
                                                                name="qcount"
                                                                value={x.qcount}
                                                                min={1}
                                                                step='1'
                                                                required
                                                                clearInput
                                                                onIonChange={(e: any) => handleinputchange(e, i)}
                                                            >
                                                            </IonInput>
                                                        </IonItem>
                                                    </div>
                                                    <div className="col-md-4 mt-4">
                                                        {
                                                            inputList.length !== 1 &&
                                                            <button onClick={() => handleremove(i)}>X</button>
                                                        }
                                                        {
                                                            inputList.length - 1 === i &&
                                                            <button onClick={() => handleaddclick()} className="mx-3 ">
                                                                <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                            </button>
                                                        }

                                                    </div>



                                                </div>
                                            );
                                        })}

                                </div>


                                <IonItem>
                                    <IonLabel position="stacked">Status</IonLabel>

                                    <IonSelect
                                        name="status"
                                        value={status}
                                        onIonChange={(e: any) => setStatus(e.target.value)}
                                    >

                                        <>
                                            <IonSelectOption value='P'>Publish</IonSelectOption>
                                            <IonSelectOption value='D'>Draft</IonSelectOption>

                                        </>

                                    </IonSelect>

                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Duration (Minutes)</IonLabel>

                                    <IonInput type="number"
                                        name="test_time"
                                        min={1}
                                        step='1'
                                        value={duration}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setDuration(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem id="trigger">
                                    <IonLabel position="floating">Quiz Start </IonLabel>
                                    <IonInput
                                        type="text"
                                        clearInput
                                        value={format(new Date(dateTimeValue), "yyyy-MM-dd HH:mm")}
                                    ></IonInput>
                                </IonItem>
                                <IonPopover trigger="trigger" triggerAction="click">
                                    <IonContent>
                                        <IonDatetime

                                            onIonChange={(e) => setDateTimeValue(e.detail.value)}></IonDatetime>
                                    </IonContent>
                                </IonPopover>

                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="danger" onClick={() => {
                                        setOpen(false)
                                        setinputList([]);
                                    }}>
                                        Cancel
                                    </IonButton>
                                    <IonButton color="primary" onClick={updateQuiz}>
                                        Save
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </div>
            </Collapse>
            <AddQuiz collapse={collapse} level={props.level} course_name={props.course_name} onClose={() => setCollapse(false)} fetchShowCourse={() => props.fetchShowCourse()} />
            <h1 className='text-center mb-2'>List of Quiz</h1>
            <MaterialReactTable
                columns={columns}
                data={props.quiz}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                enableRowNumbers
                enableColumnOrdering
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="Edit" >
                            <IconButton aria-expanded={open} aria-controls="collapseID" onClick={() => {
                                editQuiz(row.id)
                                setOpen(!open)
                                setinputList([]);
                            }}>

                                <Edit />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="left" title="View" >
                            <IconButton onClick={() => {
                                editQuiz(row.id)
                                setModalOpen(true);
                                setQuizData(row.original)
                            }}>

                                <Visibility />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteQuiz(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add-Quiz">
                        <IconButton aria-expanded={collapse} onClick={() => {
                            setCollapse(!collapse)
                            setQuizId('')
                            reset({
                                course_name: props.course_name,
                                quiz_name: '',
                                test_time: '',
                                status: '',
                                inputlist: [{ level_name: '', qcount: '' }],
                                start_date: format(new Date(), "yyyy-MM-dd HH:mm"),
                            })
                        }}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}

            />
            <ViewQuiz
                open={modalOpen}
                onClose={() => {
                    setModalOpen(false)

                }}
                showQuiz={showQuiz}
                quizData={quizData}
            />
        </div>


    );

};


const ViewQuiz: FC<{
    onClose: () => void;

    open: boolean;
    quizData: any;
    showQuiz: any

}> = (props) => {





    return (
        <Dialog open={props.open}>
            <DialogTitle textAlign="center">View Quiz</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        //gap: '1.5rem',
                    }}
                >

                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Quiz Name</h2>
                            <p>{props.quizData.quiz_name}</p>
                        </IonLabel>
                    </IonItem>
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Quiz Start</h2>
                            <p>{props.quizData.start_date}</p>
                        </IonLabel>
                    </IonItem>
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Quiz End</h2>
                            <p>{props.quizData.test_end}</p>
                        </IonLabel>
                    </IonItem>
                    <IonItem>
                        <IonLabel>
                            <h2 className="mb-3">Quiz Duration</h2>
                            <p>{props.quizData.test_time} Minutes</p>
                        </IonLabel>
                    </IonItem>


                    <IonItem >
                        <IonLabel>
                            <h2 className="mb-3">Quiz Level and Question</h2>
                            {props.showQuiz.map((item: any, i: any) =>
                                <p className="mt-3" key={i}> {item.level_name} | {item.qcount}</p>
                            )}
                        </IonLabel>
                    </IonItem>


                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>

            </DialogActions>
        </Dialog>
    );
}
export default TchrQuiz;
