import {
    IonCard,
    IonCardContent,
    IonCardHeader,
    IonCardSubtitle, IonLabel,
    IonSegment,
    IonSegmentButton,
    IonCardTitle
} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router";
const ViewCourse: React.FC = () => {
    const history = useHistory()
    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }
    return (
        <IonCard>
            <IonCardHeader>
                <IonCardTitle>My course name</IonCardTitle>
                <IonCardSubtitle>
                    <>
                        <IonSegment onIonChange={segmentChangeHandler} value="/dashboard/view/course/:id">

                            <IonSegmentButton value="/dashboard/view/course/:id">Course</IonSegmentButton>
                            <IonSegmentButton value="/dashboard/view/course/:id">Topic</IonSegmentButton>
                            <IonSegmentButton value="/dashboard/quiz">Quiz</IonSegmentButton>
                            <IonSegmentButton value="/dashboard/level">Level</IonSegmentButton>
                            <IonSegmentButton value="/dashboard/question">Question</IonSegmentButton>

                        </IonSegment>
                    </>
                </IonCardSubtitle>
            </IonCardHeader>

            <IonCardContent>
                Here's a small text description for the card content. Nothing more, nothing less.
            </IonCardContent>
        </IonCard>



    );
}
export default ViewCourse;
