import {
    IonContent,
    IonItem,
    IonLabel,
    IonSegment,
    IonSegmentButton,
    IonRouterOutlet,
    IonCard,
    IonAccordion,
    IonAccordionGroup,
    IonText,
    IonCardContent,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import { Redirect, Route, useLocation, Link } from "react-router-dom";
import baseUrl, { api } from '../../Urls';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Button from '@mui/material/Button';

const ViewUserCourse: React.FC = () => {
    type UserCourse = {
        id: string;
    };
    const history = useHistory()
    const location = useLocation()
    const { id } = useParams<UserCourse>();
    const [course_name, setCourse_name] = useState('');
    const [courseId, setCourseId] = useState('');
    const [quiz, setQuiz] = useState([]);
    const [topic, setTopic] = useState([]);
    const [level, setLevel] = useState([]);
    const [question, setQuestion] = useState([]);
    const [topic_name, setTopic_name] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    const pathname = history.location.pathname;
    useEffect(() => {
        fetch(`${baseUrl}${api.viewCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setCourse_name(result.course_name)
                setCourseId(result.id)
                setQuiz(result.quiz)
                setTopic(result.topic)

            })

    }, []);

    return (
        <>
            <IonContent fullscreen={true}>

                <IonText color="primary">
                    <h1 className='mx-2'>{course_name}</h1>
                </IonText>

                <List quiz={quiz} topic={topic} />

            </ IonContent>

        </>
    );


}

const List: React.FC<{
    quiz: any[],
    topic: any[]

}> = (props) => {


    return (

        <>

            <IonCard >

                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Quiz</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">
                            {props.quiz.map((item: any, i: any) =>
                                <h5 key={i.toString()}>
                                    <a className='text-decoration-none' href="https://www.youtube.com/watch?v=rWFahgbHiqc" target="_blank" rel="noopener noreferrer"> {item.quiz_name}</a>
                                </h5>
                            )}
                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>
            {props.topic.map((item: any, i: any) =>
                <IonCard key={i.toString()}>

                    <IonAccordionGroup>
                        <IonAccordion value="first">
                            <IonItem slot="header" color="light">
                                <IonLabel>{item.topic_name}</IonLabel>
                            </IonItem>

                            <div className="ion-padding" slot="content">
                                {item.topic_article.length == 0 && item.topic_content.length == 0 ? 'No data preview' :
                                    <>
                                        {item.topic_article.map((ta: any, i: any) =>
                                            <IonItem key={i.toString()}>
                                                <Link className='text-decoration-none'
                                                    to={{
                                                        pathname: `/dashboard/mycourse/${item.course_id}/${ta.id}`,
                                                        state: {
                                                            artname: ta.name,
                                                            artcontent: ta.content,
                                                            course_id: item.course_id,

                                                        }
                                                    }} >
                                                    {ta.name}</Link>

                                            </IonItem>



                                        )}
                                        {item.topic_content.map((tc: any, i: any) =>
                                            <IonItem key={i.toString()}>
                                                <IonLabel>file</IonLabel>
                                            </IonItem>

                                        )}
                                    </>

                                }
                            </div>
                        </IonAccordion>
                    </IonAccordionGroup>

                </IonCard>
            )}
        </>


    );
};



export default ViewUserCourse;
