import {
    IonButton, IonButtons, IonHeader, IonTitle, IonToolbar, IonContent,
    IonIcon,
    IonItem,
    IonLabel,
    IonPopover,
    IonRouterLink,
} from '@ionic/react';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Menu,
} from '@mui/material';
import {
    exitOutline,
    personCircle,
    settingsOutline,
} from "ionicons/icons";
import { Link } from 'react-router-dom';
import { useHistory } from "react-router";
import React, { useState, useEffect } from "react";
import baseUrl, { api } from '../pages/Urls';
const HeaderButton: React.FC = () => {
    const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
    const see = Boolean(anchorEl);
    const handleClick = (event: React.MouseEvent<HTMLElement>) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };
    const [open, setOpen] = useState(false);
    const history = useHistory();
    const logout = () => {
        localStorage.removeItem("token");

    };
    return (
        <div>
            <Button
                id="demo-positioned-button"
                aria-controls={see ? 'demo-positioned-menu' : undefined}
                aria-haspopup="true"
                aria-expanded={see ? 'true' : undefined}
                onClick={handleClick}
            >
                Dashboard
            </Button>
            <Menu
                id="demo-positioned-menu"
                aria-labelledby="demo-positioned-button"
                anchorEl={anchorEl}
                open={see}
                onClose={handleClose}
                anchorOrigin={{
                    vertical: 'top',
                    horizontal: 'left',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'left',
                }}
            >
                <MenuItem >{localStorage.getItem("username")}</MenuItem>
                <MenuItem onClick={handleClose}>My account</MenuItem>
                <MenuItem onClick={logout}>Logout</MenuItem>
            </Menu>
        </div>


    );
};
const ChangePassword: React.FC<{
    onClose: () => void;
    logout: () => void;
    open: boolean;

}> = (props) => {
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [error, setError] = useState("");

    const update = () => {
        let data = {
            'password': password,
            'email': email,
            'token': localStorage.getItem("token"),
        }
        fetch(`${baseUrl}${api.changePassword}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    window.alert('Password Changed Successfully');

                    props.logout();

                }

            })
            .catch(error => console.log('error', error));


    }
    return (
        <Dialog open={props.open}
            disableEnforceFocus={true}>
            <DialogTitle textAlign="center">Change Password</DialogTitle>
            <DialogContent>
                <Stack
                    sx={{
                        width: '100%',
                        minWidth: { xs: '300px', sm: '360px', md: '400px' },
                        gap: '1.5rem',
                    }}
                >

                    <TextField sx={{ mt: 3 }}
                        type="text"
                        label="Email"
                        variant="filled"
                        focused
                        value={email}
                        onChange={(e: any) => setEmail(e.target.value)} />
                    <TextField sx={{ mt: 3 }}
                        type="password"
                        label="New Password"
                        variant="filled"
                        focused
                        value={password}
                        onChange={(e: any) => setPassword(e.target.value)} />

                </Stack>

            </DialogContent>
            <DialogActions sx={{ p: '1.25rem' }}>
                <Button onClick={props.onClose}>Cancel</Button>
                <Button color="secondary" variant="contained" onClick={update}>
                    Save
                </Button>
            </DialogActions>
        </Dialog>
    );
}
export default HeaderButton;
