import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonModal,
    IonButtons,
    IonHeader,
    IonContent,
    IonSelect,
    IonSelectOption,
    IonText,
    IonList,
    IonRadioGroup,
    IonRadio,
    IonPopover,
    IonDatetime,

} from "@ionic/react";
import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Editor } from '@tinymce/tinymce-react';
import { Collapse } from 'react-bootstrap'
import AddQuiz from './AddQuiz';
import { format } from 'date-fns';
type quizData = {
    id: string;
    start_date: Date;
    quiz_name: string;
    status: string;
    test_time: any;



};


const TchrQuiz: FC<{

    quiz: any
    level: any
    course_name: string
    fetchShowCourse: () => void
}> = (props) => {
    //const [quiz, setQuiz] = useState([]);
    const [quiz_name, setQuiz_name] = useState('');
    const [id, setId] = useState('');
    const [status, setStatus] = useState('');
    const [dateTimeValue, setDateTimeValue] = useState<any>(
        format(new Date(), "yyyy-MM-dd HH:mm")
    );
    const [duration, setDuration] = useState('');
    const [level, setLevel] = useState([]);
    const [error, setError] = useState("");
    const [open, setOpen] = useState(false);
    const [collapse, setCollapse] = useState(false);
    const [inputList, setinputList] = useState([{ level_name: '', qcount: '' }]);
    const handleinputchange = (e: any, index: any) => {
        const { name, value } = e.target;
        const list: any[] = [...inputList];
        list[index][name] = value;
        setinputList(list);


    }
    const handleaddclick = () => {

        const abc = [...inputList, { level_name: '', qcount: '' }]

        setinputList(abc);
    }
    const handleremove = (index: any) => {
        const list = [...inputList];
        list.splice(index, 1);
        setinputList(list);
    }


    const columns = useMemo<MRT_ColumnDef<quizData>[]>(
        () => [



            {
                accessorKey: 'quiz_name',
                header: 'Quiz Name',

            },
            {
                accessorKey: 'status',
                header: 'Status',
                Cell: ({ cell }) => (
                    cell.getValue<string>() === "P" ?
                        'Publish' : 'Draft'
                ),
            },
            {
                accessorKey: 'start_date',
                header: 'Start Date',

            },
            {
                accessorKey: 'test_time',
                header: 'Duration (Minutes)',

            },


        ],
        [],
    );



    const editQuiz = (id: any) => {

        fetch(`${baseUrl}${api.showQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(result => {
                setQuiz_name(result.quiz.quiz_name);
                setStatus(result.quiz.status);
                setDateTimeValue(result.quiz.start_date);
                setDuration(result.quiz.test_time);
                setLevel(result.level);
                setId(result.quiz.id)
                let levelList: any[] = [];
                {
                    result.quiz.qlevel.map((item: any, i: any) =>
                        levelList.push({
                            level_name: item.level_name,
                            qcount: item.qcount

                        })
                    )
                }
                setinputList([...levelList]);

            })
            .catch(function (error) {
                console.log(error);
            })


    }
    const updateQuiz = () => {
        let data = {
            'quiz_name': quiz_name,
            'status': status,
            'start_date': format(new Date(dateTimeValue), "yyyy-MM-dd HH:mm"),
            'test_time': duration,
            'inputlist': inputList,
        }
        fetch(`${baseUrl}${api.updateQuiz}${id}`, {
            method: 'PUT',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify(data),
        })

            .then(res => {
                if (res.status === 200) {
                    props.fetchShowCourse();
                    setOpen(false)
                    setinputList([]);
                }

            })
            .catch(error => console.log('error', error));


    }
    const deleteQuiz = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteQuiz}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {

                        props.fetchShowCourse();
                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    return (

        <div className="container mt-2">
            <IonItem>
                <IonLabel color="primary">
                    <h1>{props.course_name}</h1>
                </IonLabel>
            </IonItem>
            <Collapse in={open}>
                <div id="collapseID">
                    <div className="container mt-5 mb-5 ">

                        <IonCard>
                            <IonCardHeader>
                                <IonCardTitle>
                                    <IonToolbar>
                                        <IonTitle><h1 className="text-center display-6">Edit Quiz</h1></IonTitle>
                                    </IonToolbar>
                                </IonCardTitle>
                            </IonCardHeader>
                            <hr />
                            <IonCardContent>
                                <IonItem>
                                    <IonLabel position="stacked">Quiz Name</IonLabel>
                                    <IonInput type="text"
                                        name="quiz_name"
                                        value={quiz_name}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setQuiz_name(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem>
                                    <IonText color="primary">
                                        <h2 className="mt-3 mb-3">Add Quiz Level</h2>
                                    </IonText>

                                </IonItem>
                                <div>

                                    {
                                        inputList.map((x, i) => {

                                            return (
                                                <div key={i.toString()} className="row mb-3 mt-3">
                                                    <div className=" col-md-4">
                                                        <IonItem>
                                                            <IonLabel position="stacked">Level</IonLabel>
                                                            <IonSelect
                                                                name="level_name"
                                                                value={x.level_name}
                                                                onIonChange={(e: any) => handleinputchange(e, i)}

                                                            >
                                                                {level.map((item: any, index: any) => (
                                                                    < IonSelectOption key={index.toString()}>{item.level_name}</IonSelectOption>
                                                                ))}
                                                            </IonSelect>
                                                        </IonItem>
                                                    </div>
                                                    <div className=" col-md-4">
                                                        <IonItem>
                                                            <IonLabel position="stacked">No of Question</IonLabel>
                                                            <IonInput type="number"
                                                                name="qcount"
                                                                value={x.qcount}
                                                                min={1}
                                                                step='1'
                                                                required
                                                                clearInput
                                                                onIonChange={(e: any) => handleinputchange(e, i)}
                                                            >
                                                            </IonInput>
                                                        </IonItem>
                                                    </div>
                                                    <div className="col-md-4 mt-4">
                                                        {
                                                            inputList.length !== 1 &&
                                                            <button onClick={() => handleremove(i)}>X</button>
                                                        }
                                                        {
                                                            inputList.length - 1 === i &&
                                                            <button onClick={() => handleaddclick()} className="mx-3 ">
                                                                <i className="bi bi-plus-circle-fill" title="add-option"></i>
                                                            </button>
                                                        }

                                                    </div>



                                                </div>
                                            );
                                        })}

                                </div>


                                <IonItem>
                                    <IonLabel position="stacked">Status</IonLabel>

                                    <IonSelect
                                        name="status"
                                        value={status}
                                        onIonChange={(e: any) => setStatus(e.target.value)}
                                    >

                                        <>
                                            <IonSelectOption value='P'>Publish</IonSelectOption>
                                            <IonSelectOption value='D'>Draft</IonSelectOption>

                                        </>

                                    </IonSelect>

                                </IonItem>
                                <IonItem>
                                    <IonLabel position="stacked">Duration (Minutes)</IonLabel>

                                    <IonInput type="number"
                                        name="test_time"
                                        min={1}
                                        step='1'
                                        value={duration}
                                        required
                                        clearInput
                                        onIonChange={(e: any) => setDuration(e.target.value)}
                                    >
                                    </IonInput>
                                </IonItem>
                                <IonItem id="click-trigger">
                                    <IonLabel position="floating">Quiz Start </IonLabel>
                                    <IonInput
                                        type="text"
                                        clearInput
                                        value={format(new Date(dateTimeValue), "yyyy-MM-dd HH:mm")}
                                    ></IonInput>
                                </IonItem>
                                <IonPopover trigger="click-trigger" triggerAction="click">
                                    <IonContent>
                                        <IonDatetime

                                            onIonChange={(e) => setDateTimeValue(e.target.value)}></IonDatetime>
                                    </IonContent>
                                </IonPopover>

                                <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                    <IonButton color="danger" onClick={() => {
                                        setOpen(false)
                                        setinputList([]);
                                    }}>
                                        Cancel
                                    </IonButton>
                                    <IonButton color="primary" onClick={updateQuiz}>
                                        Save
                                    </IonButton>

                                </div>
                            </IonCardContent>
                        </IonCard>

                    </div>

                </div>
            </Collapse>
            <AddQuiz collapse={collapse} level={props.level} course_name={props.course_name} onClose={() => setCollapse(false)} fetchShowCourse={() => props.fetchShowCourse()} />
            <h1 className='text-center mb-2'>List of Quiz</h1>
            <MaterialReactTable
                columns={columns}
                data={props.quiz}
                getRowId={(row => `${row.id}`)}
                enableRowActions
                enableRowNumbers
                enableColumnOrdering
                renderRowActions={({ row, table }) => (
                    <Box sx={{ display: 'flex', gap: '1rem' }}>
                        <Tooltip arrow placement="left" title="Edit" >
                            <IconButton aria-expanded={open} aria-controls="collapseID" onClick={() => {
                                editQuiz(row.id)
                                setOpen(!open)
                                setinputList([]);
                            }}>

                                <Edit />
                            </IconButton>
                        </Tooltip>
                        <Tooltip arrow placement="right" title="Delete">
                            <IconButton color="error" onClick={() => deleteQuiz(row.id)}>
                                <Delete />
                            </IconButton>
                        </Tooltip>
                    </Box>
                )}
                renderTopToolbarCustomActions={() => (
                    <Tooltip title="Add-Quiz">
                        <IconButton aria-expanded={collapse} onClick={() => setCollapse(!collapse)}>
                            Create <Add />
                        </IconButton>
                    </Tooltip>
                )}

            />

        </div>


    );

};


export default TchrQuiz;
