import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
    IonDatetime,
    IonNote,


} from "@ionic/react";
import React, { useState, useEffect } from 'react';
import baseUrl, { api } from "../../../pages/Urls";
import { Collapse } from 'react-bootstrap'
import { useForm, Controller } from "react-hook-form";
import {
    TextField,
    Box,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
    Checkbox,
    ListItemText,
    OutlinedInput,
    Typography,
    Card,
    CardContent,
    CardHeader,
} from "@mui/material";
const AddCourse: React.FC<{

    collapse: boolean
    instructor: any[]
    onClose: () => void;
    fetchCourse: () => void;

}> = (props) => {


    const {
        handleSubmit,
        control,
        setValue,
        register,
        getValues,
        formState: { errors }
    } = useForm({
        defaultValues: {
            course_name: '',
            description: '',
            crs_inst: [],

        }
    });


    const addCourse = (e: any) => {
        {
            e.preventDefault();



            fetch(`${baseUrl}${api.addCourse}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {

                        props.onClose();
                        props.fetchCourse();

                    }

                    else {
                        throw new Error("Status code: " + response.status);
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    // console.log(error.course_name)

    return (
        <Collapse in={props.collapse}>

            <div className="container mt-5 mb-5 ">
                <Card>

                    <CardContent>

                        <Box
                            component="form"
                            sx={{
                                '& .MuiTextField-root': { m: 1, width: '100ch' },
                            }}
                            autoComplete="off"
                            onSubmit={handleSubmit(addCourse)}
                        >

                            <TextField
                                required
                                id="filled-required"
                                label="Course Name"
                                variant="filled"
                            />
                            <TextField
                                label="Course Description"
                                variant="filled"
                            />
                            <FormControl sx={{ m: 1, width: 300 }}>
                                <InputLabel >Instructors</InputLabel>
                                <Select
                                    multiple
                                    input={<OutlinedInput label="Tag" />}
                                // renderValue={(selected) => selected.join(', ')}
                                //MenuProps={MenuProps}
                                >
                                    {props.instructor.map((item, i) => (
                                        <MenuItem key={i} value={item.name}>

                                            <ListItemText primary={item.name} />
                                        </MenuItem>
                                    ))}
                                </Select>
                            </FormControl>

                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="danger" onClick={props.onClose}>
                                    Cancel
                                </IonButton>
                                <IonButton color="primary" type="submit">
                                    Save
                                </IonButton>

                            </div>


                        </Box>

                    </CardContent>
                </Card>

            </div>


        </Collapse >
    );
}

export default AddCourse;
