import {
    IonContent,
    IonItem,
    IonLabel,
    IonSegment,
    IonSegmentButton,
    IonRouterOutlet,
    IonCard,
    IonAccordion,
    IonAccordionGroup,
    IonText,
    IonCardContent,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import { Redirect, Route, useLocation, Link } from "react-router-dom";
import baseUrl, { api } from '../../../pages/Urls';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Button from '@mui/material/Button';
const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
};

const UserCourse: React.FC = () => {
    type UserCourse = {
        id: string;
    };
    const history = useHistory()
    const location = useLocation()
    const { id } = useParams<UserCourse>();
    const [course_name, setCourse_name] = useState('');
    const [courseId, setCourseId] = useState('');
    const [quiz, setQuiz] = useState([]);
    const [topic, setTopic] = useState([]);
    const [level, setLevel] = useState([]);
    const [question, setQuestion] = useState([]);
    const [topic_name, setTopic_name] = useState('');
    const [quiz_name, setQuiz_name] = useState('');
    const pathname = history.location.pathname;
    useEffect(() => {
        fetch(`${baseUrl}${api.viewCourse}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setCourse_name(result.course_name)
                setCourseId(result.id)
                setQuiz(result.quiz)
                setTopic(result.topic)

            })

    }, []);

    return (
        <>
            <IonContent fullscreen={true}>

                <IonText color="primary">
                    <h1 className='mx-2'>{course_name}</h1>
                </IonText>

                <List quiz={quiz} topic={topic} />

            </ IonContent>

        </>
    );


}

const List: React.FC<{
    quiz: any[],
    topic: any[]

}> = (props) => {
    const [open, setOpen] = React.useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    return (

        <>

            <IonCard >

                <IonAccordionGroup>
                    <IonAccordion value="first">
                        <IonItem slot="header" color="light">
                            <IonLabel>Quiz</IonLabel>
                        </IonItem>

                        <div className="ion-padding" slot="content">
                            {props.quiz.map((item: any, i: any) =>
                                <h5 key={i.toString()}>
                                    <Link className='text-decoration-none' to={`/dashboard/mycourse/${item.course_id}`}> {item.quiz_name}</Link>
                                </h5>
                            )}
                        </div>
                    </IonAccordion>
                </IonAccordionGroup>

            </IonCard>
            {props.topic.map((item: any, i: any) =>
                <IonCard key={i.toString()}>

                    <IonAccordionGroup>
                        <IonAccordion value="first">
                            <IonItem slot="header" color="light">
                                <IonLabel>{item.topic_name}</IonLabel>
                            </IonItem>
                            {/* <ArticleModal open={open} handleClose={handleClose} /> */}
                            <div className="ion-padding" slot="content">

                                {item.topic_article.map((ta: any, i: any) =>

                                    <IonAccordionGroup color="primary">
                                        <IonAccordion value="first">
                                            <IonItem slot="header" color="light">
                                                <IonLabel>{ta.name}</IonLabel>
                                            </IonItem>
                                            <div className="ion-padding" slot="content">
                                            </div>
                                        </IonAccordion>
                                    </IonAccordionGroup>


                                )}
                                {item.topic_content.map((tc: any, i: any) =>
                                    <IonItem key={i.toString()}>
                                        <IonLabel>{tc.file}</IonLabel>
                                    </IonItem>

                                )}

                            </div>
                        </IonAccordion>
                    </IonAccordionGroup>

                </IonCard>
            )}
        </>


    );
};

