import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    Button,
    Dialog,
    DialogActions,
    DialogContent,
    DialogTitle,
    IconButton,
    MenuItem,
    Stack,
    TextField,
    Tooltip,
} from '@mui/material';
import { Delete, Edit, Add } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { Collapse } from 'react-bootstrap'
import { Editor } from '@tinymce/tinymce-react';
import { Typography } from '@mui/material';
type articleData = {
    id: string;
    name: string;
};

//nested data is ok, see accessorKeys in ColumnDef below


const TopicArticle: FC<{
    fetchViewTopic: () => void;
    topicArticle: any
}> = (props) => {
    const [articleData, setArticleData] = useState({});
    const [collapse, setCollapse] = useState(false);
    const columns = useMemo<MRT_ColumnDef<articleData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                enableColumnOrdering: false,
                size: 80,
            },
            {
                accessorKey: 'name',
                header: 'Course Name',

            },


        ],
        [],
    );

    const deleteTopicArticle = (id: any) => {
        if (window.confirm('Are you sure?')) {
            fetch(`${baseUrl}${api.deleteTopicArticle}${id}`, {
                method: 'DELETE',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                }

            })

                .then(res => res.json())
                .then(res => {
                    if (res) {
                        props.fetchViewTopic();

                    }

                })
                .catch(error => console.log('error', error));
        }
    }
    console.log(articleData)
    return (
        <>
            <div className="container mt-5">
                <UpdateArticle
                    collapse={collapse}
                    onClose={() => setCollapse(false)}
                    fetchViewTopic={() => props.fetchViewTopic()}
                    articleData={articleData}
                />
                <h1 className='text-center mb-2'>List of Topic Articles</h1>
                <MaterialReactTable
                    columns={columns}
                    data={props.topicArticle}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>
                            <Tooltip arrow placement="left" title="Edit">
                                <IconButton aria-expanded={collapse} onClick={() => {
                                    setArticleData(row.original)
                                    setCollapse(!collapse);
                                }}>
                                    <Edit />
                                </IconButton>
                            </Tooltip>
                            <Tooltip arrow placement="right" title="Delete">
                                <IconButton color="error" onClick={() => deleteTopicArticle(row.id)}>
                                    <Delete />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}
                    renderTopToolbarCustomActions={() => (
                        <Tooltip title="Add-Course">
                            <IconButton >
                                Create <Add />
                            </IconButton>
                        </Tooltip>
                    )}
                />

            </div>
        </>

    );


};

const UpdateArticle: FC<{
    onClose: () => void;
    fetchViewTopic: () => void;
    collapse: boolean;
    articleData: any
}> = (props) => {



    useEffect(() => {


    }, [props.articleData]);

    return (
        <Collapse in={props.collapse}>

            <div>

            </div>

        </Collapse>
    );
}
export default TopicArticle;
