    IonCardHeader,
    IonTitle,
    IonToolbar,
    IonButtons,
    IonButton,
    IonCardContent,
    IonCard,

} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from "react-router";
import baseUrl, { api } from '../../../pages/Urls';

const ViewBlog: React.FC<{
    title: string
}> = (props) => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [date, setDate] = useState('');
    const history = useHistory();

    const back = () => {
        history.push("/dashboard/blog")
    }

    return (
        <>

            <IonCard>

                <IonCardHeader>
                    <IonToolbar>
                        <div className="text-center">
                            <IonTitle >
                                {title}
                            </IonTitle>
                        </div>
                        <IonButtons slot="start">
                            <IonButton onClick={back} > <i className="bi bi-backspace"> Back</i> </IonButton>
                        </IonButtons>
                    </IonToolbar>
                </IonCardHeader>


                <IonCardContent>

                    <h1 color="dark">{title}</h1>


                    <h2>Content</h2>

                    <p>date</p>


                </IonCardContent>



            </IonCard>

        </>

    );
};
export default ViewBlog;
