import {
    IonHeader,
    IonTitle,
    IonToolbar,
    IonContent,
    IonItem,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,

} from '@ionic/react';

import React, { useState, useEffect } from 'react';
import baseUrl, { api } from '../../../pages/Urls';
import { useHistory, useParams } from "react-router";
const ViewQuiz: React.FC = () => {
    type ViewQuizParams = {
        id: string;
    };
    const [quiz, setQuiz] = useState([]);
    const [quiz_name, setQuiz_name] = useState('');
    const [level_id, setLevel_id] = useState('');
    const [level_name, setLevel_name] = useState('');
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(1);
    const history = useHistory();
    const { id } = useParams<ViewQuizParams>();
    const back = () => {
        history.push("/dashboard/quiz")
    }

    useEffect(() => {

        fetch(`${baseUrl}${api.editQuiz}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                // setQuiz_name(result.quiz.quiz_name);
                setQuiz_name(result.quiz.map((q: any) => (q.quiz_name)));
                setLevel_name(result.level_name);
                setLevel_id(result.level_id)
            })
            .catch(function (error) {
                console.log(error);
            })

    }, [])
    return (
        <>
            <IonContent>
                <IonToolbar>
                    <div className="text-center">
                        <IonTitle >
                            {quiz_name} Levels
                        </IonTitle>
                    </div>
                    <IonButtons slot="start">
                        <IonButton onClick={back} > <i className="bi bi-arrow-left"></i> </IonButton>
                    </IonButtons>
                </IonToolbar>

            </IonContent>

        </>

    );
}
export default ViewQuiz;
