    IonCard,
    IonCardContent,
    IonCardHeader,
    IonCardSubtitle, IonLabel,
    IonSegment,
    IonSegmentButton,
    IonCardTitle, IonContent, IonHeader, IonMenu, IonSplitPane, IonTitle, IonToolbar, IonMenuToggle, IonButton, IonItem, IonList, IonRouterOutlet
} from '@ionic/react';
import React, { useState, useEffect } from 'react';
import { useHistory } from "react-router";

const ViewCourse: React.FC = () => {
    const history = useHistory()
    const segmentChangeHandler = (event: CustomEvent) => {
        history.push(event.detail.value)
    }

    return (
        <>
            <h1>Nav</h1>
            <IonSplitPane when="xs" contentId="main">
                <IonMenu contentId="main">
                    <IonHeader>
                        <IonToolbar color="tertiary">
                            <IonTitle>Menu</IonTitle>
                        </IonToolbar>
                    </IonHeader>
                    <IonContent className="ion-padding">
                        Menu Content
                    </IonContent>
                </IonMenu>

                <div className="ion-page" id="main">
                    <IonHeader>
                        <IonToolbar>
                            <IonTitle>Main View</IonTitle>
                        </IonToolbar>
                    </IonHeader>
                    <IonContent className="ion-padding">
                        Main View Content
                    </IonContent>
                </div>
            </IonSplitPane></>

    );
}
export default ViewCourse;
