import {
    IonButton, IonButtons, IonHeader, IonTitle, IonToolbar, IonContent,
    IonIcon,
    IonItem,
    IonLabel,
    IonPopover,
    IonRouterLink,
} from '@ionic/react';
import {
    exitOutline,
    personCircle,
    settingsOutline,
} from "ionicons/icons";
import '../pages/Style.css';
import React, { useState, useEffect } from 'react';
import { useHistory, useLocation } from "react-router";
import HeaderButton from './HeaderButton';
const Nav: React.FC<{
    isAuth: boolean;
}> = (props) => {
    const [active, setActive] = useState("");
    const location = useLocation()
    const history = useHistory();
    const loginPage = (e: any) => {
        //setActive(e.target.id);
        history.push("/login");
    };
    const signUpPage = (e: any) => {
        //setActive(e.target.id);
        history.push("/signup");
    };
    const homePage = (e: any) => {
        //setActive(e.target.id);
        history.push("/home");
    };
    return (

        <IonHeader>
            <IonToolbar>
                <IonTitle>E-learning</IonTitle>
                {!props.isAuth ?
                    <IonButtons slot="secondary">
                        <IonButton
                            className={location.pathname === '/home' ? "selected" : ''}
                            id={"1"} onClick={homePage} >Home</IonButton>
                        <IonButton
                            className={active === "2" ? "active" : undefined}
                            id={"2"} onClick={loginPage}>Sign-In</IonButton>
                        <IonButton
                            className={active === "3" ? "active" : undefined}
                            id={"3"} onClick={signUpPage}>Sign-Up</IonButton>
                    </IonButtons> :
                    <HeaderButton />}
            </IonToolbar>
        </IonHeader >

    );
};

export default Nav;
