import React, { FC, useMemo, useState, useEffect, useRef } from 'react';
import { useForm } from "react-hook-form";
import MaterialReactTable, {
    MaterialReactTableProps,
    MRT_Cell,
    MRT_ColumnDef,
    MRT_Row,
} from 'material-react-table';
import {
    Box,
    IconButton,
    Tooltip,
} from '@mui/material';
import { Input } from '@mui/icons-material';
import baseUrl, { api } from '../../pages/Urls';
import { IonContent } from '@ionic/react';
type listData = {
    id: string;
    quiz_name: string;
    name: string;
    total_point: string;
    total_question: string;

};

//nested data is ok, see accessorKeys in ColumnDef below


const QuizAttemptedList: FC<{

    courseId: string

}> = (props) => {
    const [attemptList, setAttemptList] = useState([]);
    const [courseData, setCourseData] = useState({});
    const [modalOpen, setModalOpen] = useState(false);

    const fetchAtmList = () => {
        fetch(`${baseUrl}${api.attemptHistory}${props.courseId}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(resp => resp.json())
            .then(result => {
                setAttemptList(result)

            })
    }
    useEffect(() => {
        fetchAtmList();

    }, [])

    const columns = useMemo<MRT_ColumnDef<listData>[]>(
        () => [

            {
                accessorKey: 'id', //normal accessorKey
                header: 'ID',
                enableEditing: false,
                enableColumnOrdering: false,
                size: 80,
            },
            {
                accessorKey: 'quiz_name',
                header: 'Quiz Name',

            },
            {
                accessorKey: 'name',
                header: 'User Name',

            },
            {
                accessorKey: 'total_point',
                header: 'Total Mark',

            },
            {
                accessorKey: 'total_question',
                header: 'Total Mark',

            },


        ],
        [],
    );

    const reAttempt = (id: any) => {
        if (window.confirm('Are you sure?')) {
            let data = {
                'is_attempt': 0,
            }
            fetch(`${baseUrl}${api.reAttempt}${id}`, {
                method: 'PUT',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`

                },
                body: JSON.stringify(data),

            }).then(res => {
                if (res.status === 200) {

                    window.location.href = `/dashboard/mycourse/${props.courseId}/attempt/list`;
                }

            })
                .catch(error => console.log('error', error));

        }
    }
    return (
        <IonContent>
            <div className="container mt-5">
                <h1 className='text-center mb-2'>List of Courses</h1>
                <MaterialReactTable
                    columns={columns}
                    data={attemptList}
                    getRowId={(row => `${row.id}`)}
                    enableRowActions
                    enableColumnOrdering
                    renderRowActions={({ row, table }) => (
                        <Box sx={{ display: 'flex', gap: '1rem' }}>

                            <Tooltip arrow placement="right" title="Enrole-course">
                                <IconButton style={{ color: "green" }} onClick={() => reAttempt(row.id)}>
                                    <Input />
                                </IconButton>
                            </Tooltip>
                        </Box>
                    )}

                />

            </div>
        </IonContent>

    );

};

export default QuizAttemptedList;
