import {
    IonHeader,
    IonPage,
    IonTitle,
    IonToolbar,
    IonContent,
    IonMenu,
    IonList,
    IonItem,
    IonRouterOutlet,
    IonMenuButton,
    IonButtons,
    IonButton,
    IonCardContent,
    IonModal,
    IonSearchbar,

} from '@ionic/react';
import React, { Component, useState } from "react";
import { Redirect } from 'react-router-dom';
import baseUrl, { api } from './Urls';
import './Blog.css';
import { search } from 'ionicons/icons';

class Blog extends Component<any, any>{
    constructor(props: any) {
        super(props);

        this.state = {

            redirectToReferrer: false,
            token: null,

        };
        this.logout = this.logout.bind(this);

    }


    componentWillMount() {

        if (localStorage.getItem('token')) {
            this.setState({
                token: localStorage.getItem("token")
            });

        }

        else {
            this.setState({ redirectToReferrer: true });
        }


    }

    logout() {
        localStorage.setItem('token', '');
        localStorage.clear();
        this.setState({ redirectToReferrer: true });
    }


    render() {
        if (this.state.redirectToReferrer) {
            return (<Redirect to={'/login'} />)
        }
        return (
            <IonPage>
                <IonHeader>
                    <IonToolbar>
                        <IonButtons slot="start">
                            <IonMenuButton menu="first"></IonMenuButton>
                        </IonButtons>

                        <IonTitle>Finblog</IonTitle>

                    </IonToolbar>
                </IonHeader>
                <IonMenu side="start" menuId="first" contentId="main">
                    <IonHeader>
                        <IonToolbar color="medium">
                            <IonTitle>User Dashboard</IonTitle>
                        </IonToolbar>
                    </IonHeader>
                    <IonContent>
                        <IonList>

                            <IonItem>Menu Item</IonItem>
                            <IonItem>Menu Item</IonItem>
                            <IonItem onClick={(e: any) => this.logout()}>logout</IonItem>
                        </IonList>
                    </IonContent>
                </IonMenu>
                <IonRouterOutlet id="main"></IonRouterOutlet>
                <IonContent>

                    <ShowArticle />
                </IonContent>

            </IonPage>
        );
    }
}

class Pagination extends React.Component<any, any> {
    constructor(props: any) {
        super(props);
        this.state = {

            currentPage: 1,


        };
    }
    render() {
        const { currentPage } = this.state;
        const { articlesPerPage, totalArticles, paginate, nextPage, prevPage } = this.props;
        const pageNumbers = [];

        for (let i = 1; i <= Math.ceil(totalArticles / articlesPerPage); i++) {
            pageNumbers.push(i);
        }

        return (
            <nav>
                <ul className="pagination justify-content-center">
                    <li className={currentPage === 1 ? 'disabled' : ''}>
                        <a className="page-link" onClick={() => prevPage()}>Previous</a>
                    </li>
                    {pageNumbers.map(num => (
                        <li key={num}>
                            <a onClick={() => paginate(num)} className="page-link">{num}</a>
                        </li>
                    ))}
                    <li className={currentPage === 1 ? 'disabled' : ''}>
                        <a className="page-link" onClick={() => nextPage()}>Next</a>
                    </li>
                </ul>
            </nav>
        )
    }
}

class ShowArticle extends React.Component<any, any>{
    constructor(props: any) {
        super(props);
        this.state = {
            articles: [],
            redirectToReferrer: false,
            show: false,
            title: "",
            slug: "",
            content: "",
            status: "",
            date: "",
            id: '',
            currentPage: 1,
            articlesPerPage: 10,
            query: "",
        };

        this.showModal = this.showModal.bind(this);
        this.hideModal = this.hideModal.bind(this);

    }
    showModal(id: any) {
        this.setState({
            show: true,
        });

        fetch(`${baseUrl}${api.viewArticle}${id}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
        })
            .then(res => res.json())
            .then(result => {
                this.setState((result));
            })
            .catch(function (error) {
                console.log(error);
            })
    };

    hideModal() {
        this.setState({ show: false });
    };


    componentWillMount() {

        fetch(`${baseUrl}${api.allArticle}`, {
            method: 'GET',
            headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`

            }
        })
            .then(res => res.json())
            .then(data => {

                this.setState({ articles: data.reverse() });

            })
            .catch(error => console.log('error', error));
    }




    render() {
        const { currentPage, articlesPerPage, query } = this.state;
        const { articles, show, title, content, date } = this.state;
        const indexOfLastPost = currentPage * articlesPerPage;
        const indexOfFirstPost = indexOfLastPost - articlesPerPage;

        const currentArticles = articles.slice(indexOfFirstPost, indexOfLastPost);

        const paginate = (pageNum: any) => this.setState({ currentPage: pageNum });

        const nextPage = (e: any) => {
            this.setState({ currentPage: currentPage + 1 });
        }

        const prevPage = (e: any) => {
            this.setState({ currentPage: currentPage - 1 });

        }


        return (
            <>
                <div className="container mt-3">
                    <h2>Recent Articles Post</h2>
                    <hr />

                    <div>
                        <SearchBar placeholder="Enter title..." data={articles} />
                    </div>

                    <div>
                        {
                            currentArticles.map((item: any, i: any) =>
                                <div className="card mb-3">
                                    <div className="mt-3 mx-3" >

                                        <h4 className="text-primary" onClick={(e) => this.showModal(item.id)}>{item.title}</h4></div>

                                    <>

                                        <IonModal isOpen={show} class="fullscreen">

                                            <IonHeader>
                                                <IonToolbar>
                                                    <div className="text-center">
                                                        <IonTitle >{title}
                                                        </IonTitle>
                                                    </div>
                                                    <IonButtons slot="start">
                                                        <IonButton onClick={this.hideModal} > <i className="bi bi-backspace"> Back</i> </IonButton>
                                                    </IonButtons>
                                                </IonToolbar>
                                            </IonHeader>

                                            <IonContent>
                                                <IonCardContent>
                                                    <IonItem >
                                                        <h1 color="dark">{title}</h1>

                                                    </IonItem>
                                                    <IonItem>
                                                        <h2 dangerouslySetInnerHTML={{ __html: content }}></h2>
                                                    </IonItem>
                                                    <IonItem>
                                                        <p>{date}</p>

                                                    </IonItem>
                                                </IonCardContent>

                                            </IonContent>

                                        </IonModal>

                                    </>
                                    <div className="mt-2 mx-3" ><p>{item.date}</p></div>

                                </div>
                            )}

                        <div>
                            <Pagination
                                articlesPerPage={articlesPerPage}
                                totalArticles={articles.length}
                                paginate={paginate}
                                nextPage={nextPage}
                                prevPage={prevPage} />
                        </div>
                    </div>
                </div>

            </>

        );


    }

}



export default Blog;
