import React, { FC, useState } from 'react';
import baseUrl, { api } from '../../pages/Urls';
import { Collapse } from 'react-bootstrap';
import { useForm } from "react-hook-form";
import { ErrorMessage } from "@hookform/error-message";
import {

    IonButton,
    IonInput,
    IonItem,
    IonLabel,
    IonCardContent,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonToolbar,
    IonTitle,
    IonSelect,
    IonSelectOption,
} from "@ionic/react";
const AddLevel: FC<{
    onClose: () => void;
    fetchShowCourse: () => void;
    open: boolean;
    topic: any
    course_name: string
}> = (props) => {

    const [ltopic, setLTopic] = useState([]);
    const [level_name, setLevel_name] = useState('');
    const {
        handleSubmit,
        control,
        setValue,
        register,
        getValues,
        formState: { errors },
        reset,

    } = useForm({
        defaultValues: {
            level_name: '',
            course_name: props.course_name,
            ltopic: [],

        }
    });
    const addLevel = (e: any) => {
        {

            fetch(`${baseUrl}${api.addLevel}`, {
                method: 'POST',
                headers: {
                    Accept: 'application/json',
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem("token")}`
                },
                body: JSON.stringify(e),
            })

                .then(response => {
                    if (response.status === 200) {
                        props.fetchShowCourse();
                        props.onClose();
                        setLTopic([]);
                        setLevel_name('');

                    }
                    else {
                        throw Error([response.status, response.statusText].join(' '));
                    }
                })

                .catch(error => console.log('error', error));

        }
    }
    return (
        <Collapse in={props.open}>
            <div id="collapseID">
                <div className="container mt-5 mb-5 ">

                    <IonCard>
                        <IonCardHeader>
                            <IonCardTitle>
                                <IonToolbar>
                                    <IonTitle><h1 className="text-center display-6">Create Level</h1></IonTitle>
                                </IonToolbar>
                            </IonCardTitle>
                        </IonCardHeader>
                        <hr />
                        <IonCardContent>
                            <IonItem>
                                <IonLabel position="stacked">Level Name</IonLabel>
                                <IonInput type="text"
                                    name="level"
                                    value={level_name}
                                    required
                                    clearInput
                                    onIonChange={(e: any) => setLevel_name(e.target.value)}
                                >
                                </IonInput>
                            </IonItem>
                            <IonItem>
                                <IonLabel position="stacked">Topic</IonLabel>
                                <IonSelect
                                    name="topic"
                                    value={ltopic}
                                    onIonChange={(e: any) => setLTopic(e.target.value)}

                                    placeholder="Select topic" multiple={true}>
                                    {props.topic.map((item: any, i: any) => (
                                        < IonSelectOption key={i.toString()}>{item.topic_name}</IonSelectOption>
                                    ))}
                                </IonSelect>

                            </IonItem>


                            <div style={{ marginTop: "3%" }} className="text-center mb-5">
                                <IonButton color="danger" onClick={props.onClose}>
                                    Cancel
                                </IonButton>
                                <IonButton color="primary" onClick={addLevel}>
                                    Save
                                </IonButton>

                            </div>
                        </IonCardContent>
                    </IonCard>

                </div>

            </div>
        </Collapse>
    );
}
export default AddLevel;
